import React, { useState } from 'react';

const HistorialClinicoForm = ({ historial = [], onSave }) => {
  const [entries, setEntries] = useState(historial);

  const handleChange = (index, field, value) => {
    const updated = [...entries];
    updated[index] = { ...updated[index], [field]: value };
    setEntries(updated);
  };

  const handleAddEntry = () => {
    setEntries([...entries, {
      id: Date.now(),
      fecha: '',
      diagnostico: '',
      tratamiento: '',
      procedimiento: '',
      observaciones: ''
    }]);
  };

  const handleRemoveEntry = (index) => {
    const updated = [...entries];
    updated.splice(index, 1);
    setEntries(updated);
  };

  const handleSubmit = () => {
    onSave(entries);
  };

  return (
    <div className="bg-white p-4 rounded shadow space-y-4">
      <h3 className="text-lg font-semibold">Editar Historial Clínico</h3>

      {entries.map((entry, index) => (
        <div key={index} className="border p-4 rounded-md space-y-2">
          <input
            type="date"
            value={entry.fecha}
            onChange={(e) => handleChange(index, 'fecha', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded"
          />
          <input
            type="text"
            placeholder="Diagnóstico"
            value={entry.diagnostico}
            onChange={(e) => handleChange(index, 'diagnostico', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded"
          />
          <input
            type="text"
            placeholder="Tratamiento"
            value={entry.tratamiento}
            onChange={(e) => handleChange(index, 'tratamiento', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded"
          />
          <input
            type="text"
            placeholder="Procedimiento"
            value={entry.procedimiento}
            onChange={(e) => handleChange(index, 'procedimiento', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded"
          />
          <textarea
            placeholder="Observaciones"
            value={entry.observaciones}
            onChange={(e) => handleChange(index, 'observaciones', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded"
          />
          <button
            onClick={() => handleRemoveEntry(index)}
            className="text-red-600 text-sm underline"
          >
            Eliminar registro
          </button>
        </div>
      ))}

      <div className="flex justify-between items-center pt-4">
        <button
          onClick={handleAddEntry}
          className="px-4 py-2 bg-blue-100 text-blue-700 rounded"
        >
          Agregar Registro
        </button>
        <button
          onClick={handleSubmit}
          className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
        >
          Guardar Historial
        </button>
      </div>
    </div>
  );
};

export default HistorialClinicoForm;
