import React, { useState } from 'react';

const PatientList = ({ patients, onSelectPatient, selectedPatient }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const filteredPatients = patients.filter(p =>
    `${p.nombre} ${p.apellido}`.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <div className="p-4 border-b border-gray-200 bg-gray-50">
        <div className="flex flex-col md:flex-row md:items-center gap-4">
          <input
            type="text"
            placeholder="Buscar pacientes..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          />
        </div>
      </div>

      <div className="divide-y divide-gray-200 max-h-96 overflow-y-auto">
        {filteredPatients.length > 0 ? (
          filteredPatients.map(patient => (
            <div
              key={patient.id}
              className={`p-4 cursor-pointer transition-colors ${
                selectedPatient?.id === patient.id
                  ? 'bg-blue-50'
                  : 'hover:bg-gray-50'
              }`}
              onClick={() => onSelectPatient(patient)}
            >
              <p className="font-medium text-gray-800">{patient.nombre} {patient.apellido}</p>
              <p className="text-sm text-gray-500">{patient.telefono}</p>
            </div>
          ))
        ) : (
          <div className="p-8 text-center text-gray-500">
            {searchTerm ? 'No se encontraron pacientes' : 'No hay pacientes registrados'}
          </div>
        )}
      </div>
    </div>
  );
};

export default PatientList;

