import React, { useState, useEffect } from 'react';
import LayoutHeader from './components/LayoutHeader';
import PatientList from './components/PatientList';
import PatientDetailView from './components/PatientDetailView';
import CalendarView from './components/CalendarView';
import AssistantView from './components/AssistantView';
import BudgetView from './components/BudgetView';
import DashboardCard from './components/DashboardCard';
import BarChart from './components/BarChart';
import PieChart from './components/PieChart';
import WeeklyAppointments from './components/WeeklyAppointments';
import RecipeEditor from './components/RecipeEditor';
import InventoryManager from './components/InventoryManager';
import ImageManager from './components/ImageManager';
import InsuranceIntegration from './components/InsuranceIntegration';
import ReportsView from './components/ReportsView';
import AuditLog from './components/AuditLog';
import RemindersManager from './components/RemindersManager';
import WhatsAppIntegration from './components/WhatsAppIntegration';
import initialData from './mock/data';
import { initializeData, saveData } from './utils/storage';

const App = () => {
  const [currentView, setCurrentView] = useState('dashboard');
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [userRole, setUserRole] = useState('doctor');
  const [pacientes, setPacientes] = useState([]);
  const [citas, setCitas] = useState([]);
  const [presupuestos, setPresupuestos] = useState([]);
  const [recetas, setRecetas] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [showRecipeEditor, setShowRecipeEditor] = useState(false);

  useEffect(() => {
    const loadedData = initializeData(initialData);
    setPacientes(loadedData.pacientes);
    setCitas(loadedData.citas);
    setPresupuestos(loadedData.presupuestos || []);
    setRecetas(loadedData.recetas || []);

    if (loadedData.pacientes.length > 0) {
      setSelectedPatient(loadedData.pacientes[0]);
    }
    setIsLoading(false);
  }, []);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500 mb-2"></div>
          <p>Cargando datos...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <LayoutHeader />
      <div className="max-w-7xl mx-auto px-4 py-6 grid grid-cols-12 gap-6">
        <div className="col-span-3 space-y-4">
          <div className="bg-white rounded-lg shadow p-4">
            <h2 className="font-semibold mb-4">Navegación</h2>
            <nav className="space-y-2">
              <button
                className={`w-full text-left px-4 py-2 rounded-lg ${currentView === 'dashboard' ? 'bg-blue-100 text-blue-700' : 'hover:bg-gray-100'}`}
                onClick={() => setCurrentView('dashboard')}
              >
                Dashboard
              </button>
              <button
                className={`w-full text-left px-4 py-2 rounded-lg ${currentView === 'pacientes' ? 'bg-blue-100 text-blue-700' : 'hover:bg-gray-100'}`}
                onClick={() => setCurrentView('pacientes')}
              >
                Pacientes
              </button>
              <button
                className={`w-full text-left px-4 py-2 rounded-lg ${currentView === 'calendario' ? 'bg-blue-100 text-blue-700' : 'hover:bg-gray-100'}`}
                onClick={() => setCurrentView('calendario')}
              >
                Calendario
              </button>
              <button
                className={`w-full text-left px-4 py-2 rounded-lg ${currentView === 'presupuestos' ? 'bg-blue-100 text-blue-700' : 'hover:bg-gray-100'}`}
                onClick={() => setCurrentView('presupuestos')}
              >
                Presupuestos
              </button>
              <button
                className={`w-full text-left px-4 py-2 rounded-lg ${currentView === 'inventario' ? 'bg-blue-100 text-blue-700' : 'hover:bg-gray-100'}`}
                onClick={() => setCurrentView('inventario')}
              >
                Inventario
              </button>
              <button
                className={`w-full text-left px-4 py-2 rounded-lg ${currentView === 'reportes' ? 'bg-blue-100 text-blue-700' : 'hover:bg-gray-100'}`}
                onClick={() => setCurrentView('reportes')}
              >
                Reportes
              </button>
              <button
                className={`w-full text-left px-4 py-2 rounded-lg ${currentView === 'configuracion' ? 'bg-blue-100 text-blue-700' : 'hover:bg-gray-100'}`}
                onClick={() => setCurrentView('configuracion')}
              >
                Configuración
              </button>
              {userRole === 'asistente' && (
                <button
                  className={`w-full text-left px-4 py-2 rounded-lg ${currentView === 'asistente' ? 'bg-blue-100 text-blue-700' : 'hover:bg-gray-100'}`}
                  onClick={() => setCurrentView('asistente')}
                >
                  Vista Asistente
                </button>
              )}
            </nav>
          </div>
        </div>
        <div className="col-span-9 space-y-6">
          {currentView === 'dashboard' && (
            <div>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <DashboardCard 
                  title="Pacientes totales" 
                  value={pacientes.length} 
                  color="blue"
                  trend="+12%"
                />
                <DashboardCard 
                  title="Citas este mes" 
                  value={citas.length} 
                  color="green"
                  trend="+5%"
                />
                <DashboardCard 
                  title="Presupuestos" 
                  value={presupuestos.length} 
                  color="purple"
                  trend="+8%"
                />
                <DashboardCard 
                  title="Recetas" 
                  value={recetas.length} 
                  color="yellow"
                  trend="-2%"
                />
              </div>
              <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mt-6">
                <div className="lg:col-span-2">
                  <BarChart 
                    data={initialData.metricas.citasPorDia} 
                    labels={['Lun', 'Mar', 'Mié', 'Jue', 'Vie', 'Sáb', 'Dom']} 
                  />
                </div>
                <div>
                  <WeeklyAppointments citas={citas} pacientes={pacientes} />
                </div>
              </div>
              <div className="grid grid-cols-1 mt-6">
                <PieChart 
                  data={initialData.metricas.especialidades} 
                />
              </div>
            </div>
          )}{currentView === 'pacientes' && (
            <>
              <PatientList 
                patients={pacientes} 
                onSelectPatient={setSelectedPatient}
              />
              {selectedPatient && (
                <>
                  <PatientDetailView 
                    patient={selectedPatient}
                  />
                  <ImageManager patientId={selectedPatient.id} />
                  <InsuranceIntegration />
                  <WhatsAppIntegration patient={selectedPatient} />
                  <RemindersManager />
                  <div className="flex space-x-3 mt-4">
                    <button
                      onClick={() => setShowRecipeEditor(true)}
                      className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700"
                    >
                      Generar Receta
                    </button>
                  </div>
                  {showRecipeEditor && (
                    <RecipeEditor 
                      patient={selectedPatient}
                      onSaveRecipe={handleSaveRecipe}
                    />
                  )}
                </>
              )}
            </>
          )}

          {currentView === 'calendario' && (
            <CalendarView 
              citas={citas}
              pacientes={pacientes}
            />
          )}

          {currentView === 'presupuestos' && (
            <BudgetView 
              presupuestos={presupuestos}
              pacientes={pacientes}
            />
          )}

          {currentView === 'inventario' && <InventoryManager />}
          {currentView === 'reportes' && <ReportsView />}
          {currentView === 'configuracion' && <AuditLog />}
          {currentView === 'asistente' && <AssistantView />}
        </div>
      </div>
    </div>
  );
};

export default App;

// DONE
