import React, { useState } from 'react';

const Login = ({ onLogin }) => {
  const [nombre, setNombre] = useState('');
  const [clave, setClave] = useState('');

  const handleLogin = async () => {
    const res = await fetch('http://localhost:5000/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ nombre, clave })
    });

    if (res.ok) {
      const data = await res.json();
      onLogin(data);
    } else {
      alert("‚ùå Credenciales incorrectas. Intenta nuevamente.");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-100 via-white to-indigo-200 p-4">
      <div className="bg-white rounded-2xl shadow-2xl p-8 w-full max-w-md border-t-4 border-indigo-500 animate-fade-in">
        <div className="flex justify-center mb-6">
          <img
            src="/logo-rhelmu.png"
            alt="Logo RHELM√ú"
            className="w-32 h-auto"
          />
        </div>

        <h2 className="text-2xl font-extrabold text-indigo-800 text-center mb-6">
          Iniciar Sesi√≥n
        </h2>

        <div className="space-y-4">
          <div>
            <label className="text-sm font-semibold text-gray-700 flex items-center gap-1">
              üë§ Usuario
            </label>
            <input
              type="text"
              placeholder="Nombre de usuario"
              value={nombre}
              onChange={e => setNombre(e.target.value)}
              className="w-full px-4 py-2 border rounded-md bg-indigo-50 focus:outline-none focus:ring-2 focus:ring-indigo-400"
            />
          </div>

          <div>
            <label className="text-sm font-semibold text-gray-700 flex items-center gap-1">
              üîí Contrase√±a
            </label>
            <input
              type="password"
              placeholder="Contrase√±a"
              value={clave}
              onChange={e => setClave(e.target.value)}
              className="w-full px-4 py-2 border rounded-md bg-indigo-50 focus:outline-none focus:ring-2 focus:ring-indigo-400"
            />
          </div>

          <button
            onClick={handleLogin}
            className="w-full mt-4 bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 rounded-md shadow-sm transition"
          >
            Ingresar
          </button>
        </div>

        <p className="mt-6 text-xs text-center text-gray-500">
          Cl√≠nica RHELM√ú &copy; {new Date().getFullYear()} ‚Äî Todos los derechos reservados
        </p>
      </div>
    </div>
  );
};

export default Login;
