import React, { useEffect, useState } from 'react';

const WeeklyAppointments = ({ citas = [], pacientes = [] }) => {
  const [citasSemana, setCitasSemana] = useState([]);

  useEffect(() => {
    const hoy = new Date();
    hoy.setHours(0, 0, 0, 0);

    const finSemana = new Date();
    finSemana.setDate(hoy.getDate() + 7);
    finSemana.setHours(23, 59, 59, 999);

    const filtradas = citas
      .filter(cita => {
        const fechaCita = new Date(cita.fecha + 'T00:00:00');
        return (
          fechaCita >= hoy &&
          fechaCita <= finSemana &&
          (cita.estado === 'pendiente' || cita.estado === 'confirmada')
        );
      })
      .sort((a, b) => new Date(a.fecha) - new Date(b.fecha));

    setCitasSemana(filtradas);
  }, [citas]);

  const obtenerNombrePaciente = (id) => {
    const paciente = pacientes.find(p => p._id === id);
    return paciente ? paciente.nombre : 'Paciente desconocido';
  };

  return (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-lg font-semibold mb-4">Citas Programadas de la Semana</h2>

      {citasSemana.length === 0 ? (
        <p className="text-gray-600">No hay citas pendientes para los pr√≥ximos 7 d√≠as.</p>
      ) : (
        <ul className="space-y-2">
          {citasSemana.map((cita) => (
            <li key={cita._id} className="bg-indigo-50 p-3 rounded shadow-sm flex justify-between items-center">
              <div>
                üìÖ <strong>{cita.fecha}</strong> ‚è∞ {cita.hora} <br />
                üßë {obtenerNombrePaciente(cita.pacienteId)} <br />
                üè∑Ô∏è <span className="capitalize">{cita.estado}</span> ‚Äì {cita.motivo}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default WeeklyAppointments;
