import React, { useState } from 'react';

const Login = ({ onLogin }) => {
  const [nombre, setNombre] = useState('');
  const [clave, setClave] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const res = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ nombre, clave })
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Error al iniciar sesión');

      localStorage.setItem('token', data.token);
      localStorage.setItem('rol', data.usuario.rol);
      localStorage.setItem('nombre', data.usuario.nombre);
      onLogin({ rol: data.usuario.rol, nombre: data.usuario.nombre });

    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-indigo-50 px-4">
      <form
        onSubmit={handleSubmit}
        className="bg-white rounded-2xl shadow-2xl p-8 w-full max-w-md border-t-4 border-indigo-500 animate-fade-in"
      >
        {/* Logo */}
        <div className="flex justify-center mb-4">
          <img src="/logo-rhelmu.png" alt="Logo" className="w-24 h-24" />
        </div>

        <h2 className="text-xl font-bold mb-4 text-center text-indigo-700">Iniciar Sesión</h2>

        {error && <div className="text-red-500 text-sm mb-4 text-center">{error}</div>}

        <input
          type="text"
          placeholder="Nombre"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          required
          className="w-full border px-3 py-2 mb-3 rounded focus:outline-none focus:ring-2 focus:ring-indigo-300"
        />
        <input
          type="password"
          placeholder="Clave"
          value={clave}
          onChange={(e) => setClave(e.target.value)}
          required
          className="w-full border px-3 py-2 mb-4 rounded focus:outline-none focus:ring-2 focus:ring-indigo-300"
        />
        <button
          type="submit"
          className="w-full bg-indigo-600 text-white py-2 rounded hover:bg-indigo-700 transition duration-200"
        >
          Ingresar
        </button>
      </form>
    </div>
  );
};

export default Login;
