import React, { useState, useEffect } from 'react';

const ImageManager = ({ patientId }) => {
  const [images, setImages] = useState([]);
  const [uploading, setUploading] = useState(false);

  // Obtener imágenes existentes al cargar
  useEffect(() => {
    if (!patientId) return;
    fetch(`http://localhost:5000/api/imagenes/${patientId}`)
      .then(res => res.json())
      .then(data => setImages(data))
      .catch(() => alert("Error al cargar imágenes clínicas"));
  }, [patientId]);

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file || !patientId) return;

    setUploading(true);

    const formData = new FormData();
    formData.append('imagen', file);
    formData.append('patientId', patientId);
    formData.append('tipo', 'radiografía');
    formData.append('notas', '');

    try {
      const res = await fetch('http://localhost:5000/api/imagenes', {
        method: 'POST',
        body: formData
      });
      const data = await res.json();
      setImages(prev => [...prev, data]);
    } catch (error) {
      alert('❌ Error al subir la imagen');
    }

    setUploading(false);
  };

  return (
    <div className="bg-white rounded-xl shadow-sm p-6">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-xl font-semibold">Imágenes Clínicas</h2>
        <label className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 cursor-pointer">
          {uploading ? 'Subiendo...' : 'Nueva Imagen'}
          <input 
            type="file" 
            accept="image/*" 
            onChange={handleImageUpload}
            className="hidden"
            disabled={uploading}
          />
        </label>
      </div>

      {images.length === 0 ? (
        <p className="text-gray-500 text-center py-8">No hay imágenes registradas</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {images.map(img => (
            <div key={img._id || img.id} className="border rounded-lg overflow-hidden shadow-sm">
              <img 
                src={`http://localhost:5000${img.url}`} 
                alt={`Imagen clínica ${img.tipo}`}
                className="w-full h-48 object-cover"
              />
              <div className="p-3 space-y-1">
                <p className="font-medium capitalize">{img.tipo}</p>
                <p className="text-sm text-gray-500">
                  {new Date(img.fecha).toLocaleDateString()}
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ImageManager;
