import React, { useState, useEffect } from 'react';

const FichaOrtodoncia = ({ data = {}, onUpdate }) => {
  const [form, setForm] = useState(data);

  useEffect(() => {
    setForm(data || {});
  }, [data]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleControlChange = (index, value) => {
    const updated = [...(form.controles || [])];
    updated[index] = value;
    setForm((prev) => ({ ...prev, controles: updated }));
  };

  const handleAddControl = () => {
    setForm((prev) => ({
      ...prev,
      controles: [...(prev.controles || []), ""]
    }));
  };

  const handleSave = () => {
    if (onUpdate) {
      onUpdate(form);
      alert('✅ Ficha ortodoncia guardada correctamente');
    }
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow space-y-4">
      <h3 className="text-lg font-semibold">Ficha Ortodoncia</h3>
      <div>
        <label className="block text-sm font-medium">Maloclusión</label>
        <input
          type="text"
          name="maloclusion"
          value={form.maloclusion || ""}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>
      <div>
        <label className="block text-sm font-medium">Plan de Tratamiento</label>
        <input
          type="text"
          name="planTratamiento"
          value={form.planTratamiento || ""}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>
      <div>
        <label className="block text-sm font-medium">Duración</label>
        <input
          type="text"
          name="duracion"
          value={form.duracion || ""}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>
      <div>
        <label className="block text-sm font-medium">Controles</label>
        {(form.controles || []).map((ctrl, i) => (
          <input
            key={i}
            type="text"
            value={ctrl}
            onChange={(e) => handleControlChange(i, e.target.value)}
            className="w-full border px-2 py-1 rounded mb-2"
          />
        ))}
        <button
          className="text-sm bg-blue-500 text-white px-3 py-1 rounded"
          onClick={handleAddControl}
        >
          Agregar control
        </button>
      </div>
      <div>
        <button
          className="text-sm bg-green-600 text-white px-4 py-2 rounded"
          onClick={handleSave}
        >
          Guardar cambios
        </button>
      </div>
    </div>
  );
};

export default FichaOrtodoncia;
