import React, { useState } from 'react';
import MedicalRecord from './MedicalRecord';
import FichaOrtodoncia from './FichaOrtodoncia';
import ImageManager from './ImageManager';
import InsuranceIntegration from './InsuranceIntegration';
import WhatsAppIntegration from './WhatsAppIntegration';
import RemindersManager from './RemindersManager';
import RecipeEditor from './RecipeEditor';
import Odontograma from './Odontograma';
import ControlChecklist from './ControlChecklist';
import HistorialClinicoForm from './HistorialClinicoForm';

const PatientDetailView = ({ patient, onUpdateHistorial }) => {
  const [showRecipeEditor, setShowRecipeEditor] = useState(false);
  const [editingHistorial, setEditingHistorial] = useState(false);

  if (!patient) return null;

  const handleHistorialSave = async (updatedHistorial) => {
    const res = await fetch(`http://localhost:5000/api/pacientes/${patient._id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...patient, historial: updatedHistorial })
    });
    const updated = await res.json();
    onUpdateHistorial(updated.historial);
    setEditingHistorial(false);
  };

  const handleControlUpdate = async (updatedControles) => {
    try {
      const res = await fetch(`http://localhost:5000/api/pacientes/${patient._id}/controles`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ controles: updatedControles })
      });
      if (!res.ok) throw new Error('Error al actualizar controles');
      console.log('✅ Controles actualizados');
    } catch (err) {
      console.error('❌ Error actualizando controles:', err);
    }
  };

  return (
    <div className="space-y-6">
      <div className="bg-white p-4 rounded-lg shadow">
        <h2 className="text-2xl font-semibold mb-2">Detalle de {patient.nombre}</h2>
        <div className="flex space-x-3 mt-2">
          <button
            className="bg-yellow-500 text-white px-3 py-1 rounded hover:bg-yellow-600 text-sm"
            onClick={() => {
              setEditingHistorial(false);
              window.dispatchEvent(new CustomEvent("editarPaciente", { detail: patient }));
            }}
          >
            Editar Paciente
          </button>
          <button
            className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700 text-sm"
            onClick={async () => {
              if (window.confirm(`¿Eliminar al paciente ${patient.nombre}?`)) {
                await fetch(`http://localhost:5000/api/pacientes/${patient._id}`, { method: "DELETE" });
                window.dispatchEvent(new CustomEvent("eliminarPaciente", { detail: patient._id }));
              }
            }}
          >
            Eliminar Paciente
          </button>
        </div>
        <div className="text-gray-600 space-y-1 text-sm mt-4">
          <p><strong>RUT:</strong> {patient.rut}</p>
          <p><strong>Edad:</strong> {patient.edad}</p>
          <p><strong>Correo:</strong> {patient.email}</p>
          <p><strong>Teléfono:</strong> {patient.telefono}</p>
        </div>
      </div>

      {/* Historial Clínico */}
      <div className="flex justify-between items-center">
        <h3 className="text-lg font-semibold">Historial Clínico</h3>
        <button
          className="text-blue-600 text-sm underline"
          onClick={() => setEditingHistorial(!editingHistorial)}
        >
          {editingHistorial ? "Cerrar editor" : "Editar Historial"}
        </button>
      </div>

      {editingHistorial ? (
        <HistorialClinicoForm
          historial={patient.historial || []}
          onSave={handleHistorialSave}
        />
      ) : (
        <MedicalRecord patient={patient} />
      )}

      {/* Odontograma */}
      <Odontograma
        data={patient.odontograma}
        onUpdate={async (updatedOdontograma) => {
          try {
            const res = await fetch(`http://localhost:5000/api/pacientes/${patient._id}/odontograma`, {
              method: 'PATCH',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({ odontograma: updatedOdontograma })
            });
            if (!res.ok) throw new Error('Error al actualizar odontograma');
            console.log('✅ Odontograma actualizado');
          } catch (err) {
            console.error('❌ Error actualizando odontograma:', err);
          }
        }}
      />

      <ControlChecklist
        controles={patient.controles}
        onUpdate={async (updatedControles) => {
          try {
            const res = await fetch(`http://localhost:5000/api/pacientes/${patient._id}/controles`, {
              method: 'PATCH',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({ controles: updatedControles })
            });
            if (!res.ok) throw new Error('Error actualizando controles');
            const updated = await res.json();
            console.log('✅ Controles actualizados:', updated);
          } catch (err) {
            console.error('❌ Error actualizando controles:', err);
          }
        }}
      />

      {/* Ficha Ortodoncia */}
      <FichaOrtodoncia data={patient.fichaOrtodoncia} />

      {/* NUEVO: Control Checklist */}
      <ControlChecklist
        controles={patient.controles || []}
        onUpdate={handleControlUpdate}
      />

      {/* Integraciones */}
      <ImageManager patientId={patient._id} />
      <InsuranceIntegration patientId={patient._id} />
      <WhatsAppIntegration patient={patient} />
      <RemindersManager patientId={patient._id} />

      {/* Generar Receta */}
      <div className="flex space-x-3">
        <button
          onClick={() => setShowRecipeEditor(true)}
          className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700"
        >
          Generar Receta
        </button>
      </div>

      {showRecipeEditor && (
        <RecipeEditor
          patient={patient}
          onSaveRecipe={() => setShowRecipeEditor(false)}
        />
      )}
    </div>
  );
};

export default PatientDetailView;
