import React, { useState, useRef } from 'react';

const BudgetEditor = ({ patient, onSaveBudget }) => {
  const [budget, setBudget] = useState({
    id: Date.now(),
    fecha: new Date().toISOString().split('T')[0],
    pacienteId: patient?._id || '',
    items: [],
    descuento: 0,
    notas: '',
    estado: 'pendiente',
    confirmado: false
  });

  const [newItem, setNewItem] = useState({
    tratamiento: '',
    cantidad: 1,
    precio: 0,
    diente: ''
  });

  const [guardado, setGuardado] = useState(false);
  const budgetRef = useRef();

  const handleBudgetChange = (e) => {
    const { name, value } = e.target;
    setBudget(prev => ({ ...prev, [name]: value }));
  };

  const handleItemChange = (e) => {
    const { name, value } = e.target;
    setNewItem(prev => ({
      ...prev,
      [name]: name === 'cantidad' || name === 'precio' ? Number(value) : value
    }));
  };

  const addItem = () => {
    if (!newItem.tratamiento || newItem.precio <= 0) return;

    const itemWithId = {
      ...newItem,
      id: Date.now(),
      subtotal: newItem.cantidad * newItem.precio
    };

    setBudget(prev => ({
      ...prev,
      items: [...prev.items, itemWithId]
    }));

    setNewItem({
      tratamiento: '',
      cantidad: 1,
      precio: 0,
      diente: ''
    });
  };

  const removeItem = (id) => {
    setBudget(prev => ({
      ...prev,
      items: prev.items.filter(item => item.id !== id)
    }));
  };

  const calculateTotal = () => {
    const subtotal = budget.items.reduce((sum, item) => sum + item.subtotal, 0);
    const descuento = subtotal * (budget.descuento / 100);
    return {
      subtotal,
      descuento,
      total: subtotal - descuento
    };
  };
  const handlePrint = () => {
    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <html>
        <head>
          <title>Presupuesto Dental - ${patient?.nombre || ''}</title>
          <style>
            body { font-family: Arial, sans-serif; margin: 20px; }
            .header { text-align: center; margin-bottom: 20px; }
            .patient-info { margin-bottom: 20px; }
            table { width: 100%; border-collapse: collapse; margin-bottom: 20px; }
            th, td { border: 1px solid #ddd; padding: 8px; text-align: left; }
            th { background-color: #f2f2f2; }
            .totals { text-align: right; margin-top: 20px; }
            .notes { margin-top: 30px; }
          </style>
        </head>
        <body>
          <div class="header">
            <h2>Presupuesto Dental</h2>
            <p>Fecha: ${new Date(budget.fecha).toLocaleDateString('es-ES')}</p>
          </div>
          <div class="patient-info">
            <p><strong>Nombre:</strong> ${patient?.nombre || ''}</p>
            <p><strong>RUT:</strong> ${patient?.rut || ''}</p>
            <p><strong>Correo:</strong> ${patient?.email || ''}</p>
            <p><strong>Tel√©fono:</strong> ${patient?.telefono || ''}</p>
          </div>
          <table>
            <thead>
              <tr>
                <th>Tratamiento</th>
                <th>Diente</th>
                <th>Cantidad</th>
                <th>Precio Unitario</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody>
              ${budget.items.map(item => `
                <tr>
                  <td>${item.tratamiento}</td>
                  <td>${item.diente || '-'}</td>
                  <td>${item.cantidad}</td>
                  <td>$${item.precio.toLocaleString()}</td>
                  <td>$${item.subtotal.toLocaleString()}</td>
                </tr>
              `).join('')}
            </tbody>
          </table>
          <div class="totals">
            <p><strong>Subtotal:</strong> $${calculateTotal().subtotal.toLocaleString()}</p>
            <p><strong>Descuento (${budget.descuento}%):</strong> $${calculateTotal().descuento.toLocaleString()}</p>
            <p><strong>Total:</strong> $${calculateTotal().total.toLocaleString()}</p>
          </div>
          ${budget.notas ? `
            <div class="notes">
              <h3>Notas:</h3>
              <p>${budget.notas}</p>
            </div>
          ` : ''}
          <script>
            window.onload = function() {
              setTimeout(() => {
                window.print();
                window.close();
              }, 200);
            };
          </script>
        </body>
      </html>
    `);
    printWindow.document.close();
  };

  const handleGuardar = () => {
    onSaveBudget(budget);
    setGuardado(true);
  };

  const handleConfirmar = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/pacientes", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          nombre: patient?.nombre,
          rut: patient?.rut,
          telefono: patient?.telefono,
          email: patient?.email,
          fechaNacimiento: patient?.fechaNacimiento,
          edad: patient?.edad,
          genero: patient?.genero,
          tieneConvenio: patient?.tieneConvenio || false,
          promocionDescuento: patient?.promocionDescuento || '',
          odontograma: budget.items.map(i => i.diente).filter(d => d)
        })
      });

      if (res.ok) {
        alert("Presupuesto confirmado y paciente registrado.");
      } else {
        alert("Error al registrar paciente.");
      }
    } catch (error) {
      console.error("Error confirmando presupuesto:", error);
    }
  };
  return (
    <div className="bg-white rounded-xl shadow-sm p-6" ref={budgetRef}>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-xl font-semibold">Presupuesto Dental</h2>
        <div className="flex space-x-3">
          <button
            onClick={handlePrint}
            className="px-4 py-2 bg-gray-100 hover:bg-gray-200 rounded-lg flex items-center"
          >
            üñ®Ô∏è Imprimir
          </button>
          <button
            onClick={() => onSaveBudget(budget)}
            className="px-4 py-2 bg-blue-600 text-white hover:bg-blue-700 rounded-lg"
          >
            Guardar Presupuesto
          </button>
          <button
            onClick={handleConfirmar}
            className="px-4 py-2 bg-green-600 text-white hover:bg-green-700 rounded-lg"
          >
            Confirmar Presupuesto
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Fecha</label>
          <input
            type="date"
            name="fecha"
            value={budget.fecha}
            onChange={handleBudgetChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Descuento (%)</label>
          <input
            type="number"
            name="descuento"
            value={budget.descuento}
            onChange={handleBudgetChange}
            min="0"
            max="100"
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
          />
        </div>
      </div>

      <div className="mb-6">
        <h3 className="text-lg font-medium mb-3">Agregar Tratamiento</h3>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-3">
          <div>
            <label className="text-sm font-medium">Tratamiento</label>
            <input
              type="text"
              name="tratamiento"
              value={newItem.tratamiento}
              onChange={handleItemChange}
              className="w-full px-3 py-2 border rounded-lg"
              placeholder="Ej: Limpieza"
            />
          </div>
          <div>
            <label className="text-sm font-medium">Diente</label>
            <input
              type="text"
              name="diente"
              value={newItem.diente}
              onChange={handleItemChange}
              className="w-full px-3 py-2 border rounded-lg"
              placeholder="Ej: 11, 12"
            />
          </div>
          <div>
            <label className="text-sm font-medium">Cantidad</label>
            <input
              type="number"
              name="cantidad"
              value={newItem.cantidad}
              onChange={handleItemChange}
              className="w-full px-3 py-2 border rounded-lg"
              min="1"
            />
          </div>
          <div>
            <label className="text-sm font-medium">Precio</label>
            <input
              type="number"
              name="precio"
              value={newItem.precio}
              onChange={handleItemChange}
              className="w-full px-3 py-2 border rounded-lg"
              min="0"
            />
          </div>
        </div>
        <button
          onClick={addItem}
          className="mt-3 px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700"
          disabled={!newItem.tratamiento || newItem.precio <= 0}
        >
          Agregar Tratamiento
        </button>
      </div>

      {budget.items.length > 0 && (
        <div className="mb-6">
          <h3 className="text-lg font-medium mb-3">Tratamientos</h3>
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs text-gray-500 uppercase">Tratamiento</th>
                <th className="px-6 py-3 text-left text-xs text-gray-500 uppercase">Diente</th>
                <th className="px-6 py-3 text-left text-xs text-gray-500 uppercase">Cantidad</th>
                <th className="px-6 py-3 text-left text-xs text-gray-500 uppercase">Precio</th>
                <th className="px-6 py-3 text-left text-xs text-gray-500 uppercase">Subtotal</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {budget.items.map(item => (
                <tr key={item.id}>
                  <td className="px-6 py-2">{item.tratamiento}</td>
                  <td className="px-6 py-2">{item.diente}</td>
                  <td className="px-6 py-2">{item.cantidad}</td>
                  <td className="px-6 py-2">${item.precio.toLocaleString()}</td>
                  <td className="px-6 py-2">${item.subtotal.toLocaleString()}</td>
                  <td className="px-6 py-2">
                    <button onClick={() => removeItem(item.id)} className="text-red-600 hover:underline">
                      Eliminar
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="md:col-span-2">
          <label className="block text-sm font-medium mb-1">Notas</label>
          <textarea
            name="notas"
            value={budget.notas}
            onChange={handleBudgetChange}
            rows="4"
            className="w-full px-3 py-2 border rounded-lg"
            placeholder="Notas adicionales sobre el presupuesto..."
          />
        </div>
        <div className="bg-gray-50 p-4 rounded-lg">
          <h4 className="text-lg font-medium mb-3">Resumen</h4>
          <div className="space-y-2">
            <div className="flex justify-between">
              <span>Subtotal:</span>
              <span>${calculateTotal().subtotal.toLocaleString()}</span>
            </div>
            <div className="flex justify-between">
              <span>Descuento:</span>
              <span>-${calculateTotal().descuento.toLocaleString()}</span>
            </div>
            <div className="flex justify-between font-semibold pt-2 border-t">
              <span>Total:</span>
              <span>${calculateTotal().total.toLocaleString()}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BudgetEditor;
