import React from 'react';

const Odontograma = ({ data = [], onToothClick }) => {
  const fdiMap = {
    1: "1.8", 2: "1.7", 3: "1.6", 4: "1.5", 5: "1.4", 6: "1.3", 7: "1.2", 8: "1.1",
    9: "2.1", 10: "2.2", 11: "2.3", 12: "2.4", 13: "2.5", 14: "2.6", 15: "2.7", 16: "2.8",
    17: "4.8", 18: "4.7", 19: "4.6", 20: "4.5", 21: "4.4", 22: "4.3", 23: "4.2", 24: "4.1",
    25: "3.1", 26: "3.2", 27: "3.3", 28: "3.4", 29: "3.5", 30: "3.6", 31: "3.7", 32: "3.8"
  };

  const getTooth = (num) => data.find(t => t.numero === num) || { estado: 'sano' };

  const getColor = (estado) => {
    switch (estado) {
      case 'caries': return 'bg-yellow-300';
      case 'obturado': return 'bg-blue-300';
      case 'extraido': return 'bg-gray-300';
      case 'endodoncia': return 'bg-purple-300';
      case 'corona': return 'bg-green-300';
      case 'implante': return 'bg-red-300';
      default: return 'bg-white';
    }
  };

  return (
    <div className="p-4 bg-white rounded shadow space-y-6">
      <div className="grid grid-cols-8 gap-2 text-center">
        {Array.from({ length: 16 }).map((_, i) => {
          const num = i + 1;
          const tooth = getTooth(num);
          return (
            <div
              key={num}
              onClick={() => onToothClick && onToothClick(tooth)}
              className={`relative border border-gray-300 rounded-t-full h-14 flex flex-col items-center justify-center cursor-pointer ${getColor(tooth.estado)}`}
            >
              <span className="text-xs text-gray-600">{num}</span>
              <span className="text-[10px] text-red-600">{fdiMap[num]}</span>
            </div>
          );
        })}
      </div>
      <div className="grid grid-cols-8 gap-2 text-center">
        {Array.from({ length: 16 }).map((_, i) => {
          const num = i + 17;
          const tooth = getTooth(num);
          return (
            <div
              key={num}
              onClick={() => onToothClick && onToothClick(tooth)}
              className={`relative border border-gray-300 rounded-b-full h-14 flex flex-col items-center justify-center cursor-pointer ${getColor(tooth.estado)}`}
            >
              <span className="text-xs text-gray-600">{num}</span>
              <span className="text-[10px] text-red-600">{fdiMap[num]}</span>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Odontograma;
