import React, { useEffect, useState } from 'react';

const UserManager = () => {
  const [usuarios, setUsuarios] = useState([]);
  const [nuevo, setNuevo] = useState({ nombre: '', correo: '', password: '', rol: 'asistente' });

  useEffect(() => {
    fetch('http://localhost:5000/api/usuarios')
      .then(res => res.json())
      .then(setUsuarios);
  }, []);

  const handleChange = e => {
    setNuevo({ ...nuevo, [e.target.name]: e.target.value });
  };

  const crearUsuario = () => {
    fetch('http://localhost:5000/api/usuarios', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(nuevo)
    })
      .then(res => res.json())
      .then(u => {
        setUsuarios(prev => [...prev, u]);
        setNuevo({ nombre: '', correo: '', password: '', rol: 'asistente' });
      });
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Gestión de Usuarios</h2>

      <div className="grid grid-cols-2 gap-4 mb-6">
        <input name="nombre" placeholder="Nombre" value={nuevo.nombre} onChange={handleChange} className="border rounded px-3 py-2" />
        <input name="correo" placeholder="Correo" value={nuevo.correo} onChange={handleChange} className="border rounded px-3 py-2" />
        <input name="password" placeholder="Contraseña" type="password" value={nuevo.password} onChange={handleChange} className="border rounded px-3 py-2" />
        <select name="rol" value={nuevo.rol} onChange={handleChange} className="border rounded px-3 py-2">
          <option value="doctor">Doctor</option>
          <option value="contador">Contador</option>
          <option value="asistente">Asistente</option>
        </select>
      </div>

      <button onClick={crearUsuario} className="bg-blue-600 text-white px-4 py-2 rounded">Crear Usuario</button>

      <hr className="my-6" />

      <ul className="space-y-2">
        {usuarios.map(u => (
          <li key={u._id} className="border p-2 rounded">
            <strong>{u.nombre}</strong> - {u.correo} - <em>{u.rol}</em>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserManager;
