import React from 'react';

const CertificadoCompleto = ({ receta, paciente }) => {
  const fecha = new Date(receta.fecha).toLocaleDateString('es-CL');

  const handlePrint = () => {
    const printWindow = window.open('', '', 'width=800,height=1000');
    const htmlContent = `
      <html>
  <head>
    <title>Certificado y Receta Médica</title>
    <style>
      @page {
        size: A4;
        margin: 2.5cm;
      }

      body {
        font-family: Arial, sans-serif;
        font-size: 14px;
        color: #000;
        position: relative;
      }

      .header {
        text-align: center;
        margin-bottom: 20px;
      }

      .header img {
        height: 70px;
        margin-bottom: 8px;
      }

      .header h2 {
        margin: 0;
        font-size: 20px;
        font-weight: bold;
      }

      .header p {
        margin: 2px 0;
      }

      .title {
        text-align: center;
        font-size: 16px;
        font-weight: bold;
        text-decoration: underline;
        margin-top: 20px;
        margin-bottom: 20px;
      }

      .section {
        margin-bottom: 20px;
      }

      .section p {
        margin: 4px 0;
      }

      .section strong {
        display: inline-block;
        width: 140px;
      }

      ul {
        padding-left: 20px;
      }

      .marca-agua-img {
        position: fixed;
        top: 30%;
        left: 50%;
        transform: translate(-50%, -50%);
        opacity: 0.05;
        z-index: -1;
        width: 400px;
      }

      .firma {
        text-align: center;
        margin-top: 60px;
      }

      .firma p {
        margin: 2px;
      }

      .firma .linea {
        border-top: 1px solid #000;
        width: 280px;
        margin: 0 auto;
        padding-top: 5px;
      }

      .footer {
        text-align: center;
        font-size: 12px;
        margin-top: 50px;
        line-height: 1.5;
        color: #333;
      }
    </style>
  </head>
  <body>
    <img src="${window.location.origin}/logo-rhelmu.png" class="marca-agua-img" alt="Marca de agua" />

    <div class="header">
      <img src="${window.location.origin}/logo-rhelmu.png" alt="Logo Rhelmu" />
      <h2>Dr. Mauricio Hurtado Cáceres</h2>
      <p style="color:#2563eb; font-weight:bold;">Ortodoncia y Ortopedia</p>
      <p style="color:#3b82f6;">Dentomaxilofacial</p>
      <p>Rut: 12.917.335-1</p>
    </div>

    <div class="title">CERTIFICADO Y RECETA MÉDICA</div>

    <div class="section">
      <p><strong>Nombre del Paciente:</strong> ${paciente.nombre}</p>
      <p><strong>RUT:</strong> ${paciente.rut}</p>
      <p><strong>Fecha:</strong> ${fecha}</p>
      <p><strong>Vigencia:</strong> ${receta.vigencia} días</p>
    </div>

    <div class="section">
      <p><strong>Medicamentos Recetados:</strong></p>
      <ul>
        ${receta.medicamentos?.length > 0
          ? receta.medicamentos.map((m) => `
              <li>
                <strong>${m.nombre}</strong>: ${m.dosis} unidades, cada ${m.frecuencia}, durante ${m.duracion}
              </li>
            `).join('')
          : '<li>No hay medicamentos registrados.</li>'}
      </ul>
    </div>

    ${receta.indicaciones ? `
      <div class="section">
        <strong>Indicaciones adicionales:</strong>
        <p>${receta.indicaciones}</p>
      </div>
    ` : ''}

    <div class="firma">
      <div class="linea">Dr. Mauricio Hurtado Cáceres</div>
      <p>12.917.335-1</p>
    </div>

    <div class="footer">
      Dirección: Lord Cochrane 635, Torre B, piso 10, Oficina 1002, Concepción.<br />
      Teléfono: +56 944435569
    </div>
  </body>
</html>
    `;

    printWindow.document.open();
    printWindow.document.write(htmlContent);
    printWindow.document.close();
    printWindow.focus();

    printWindow.onload = () => {
      printWindow.print();
      printWindow.onafterprint = () => printWindow.close();
    };
  };

  return (
    <div className="p-4 bg-white rounded shadow">
      <div className="flex justify-end mb-4">
        <button
          onClick={handlePrint}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Imprimir Certificado + Receta
        </button>
      </div>
    </div>
  );
};

export default CertificadoCompleto;
