import React, { useState, useRef } from 'react';

const BudgetEditor = ({ patient, onSaveBudget }) => {
  const [budget, setBudget] = useState({
    id: Date.now(),
    fecha: new Date().toISOString().split('T')[0],
    pacienteId: patient?._id || '',
    items: [],
    descuento: 0,
    notas: '',
    estado: 'pendiente'
  });

  const [newItem, setNewItem] = useState({
    tratamiento: '',
    cantidad: 1,
    precio: 0,
    diente: ''
  });

  const budgetRef = useRef();

  const handleBudgetChange = (e) => {
    const { name, value } = e.target;
    setBudget(prev => ({ ...prev, [name]: value }));
  };

  const handleItemChange = (e) => {
    const { name, value } = e.target;
    setNewItem(prev => ({
      ...prev,
      [name]: name === 'cantidad' || name === 'precio' ? Number(value) : value
    }));
  };

  const addItem = () => {
    if (!newItem.tratamiento || newItem.precio <= 0) return;

    const itemWithId = {
      ...newItem,
      id: Date.now(),
      subtotal: newItem.cantidad * newItem.precio
    };

    setBudget(prev => ({
      ...prev,
      items: [...prev.items, itemWithId]
    }));

    setNewItem({
      tratamiento: '',
      cantidad: 1,
      precio: 0,
      diente: ''
    });
  };

  const removeItem = (id) => {
    setBudget(prev => ({
      ...prev,
      items: prev.items.filter(item => item.id !== id)
    }));
  };

  const calculateTotal = () => {
    const subtotal = budget.items.reduce((sum, item) => sum + item.subtotal, 0);
    const descuento = subtotal * (budget.descuento / 100);
    return {
      subtotal,
      descuento,
      total: subtotal - descuento
    };
  };

  const handlePrint = () => {
    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <html>
        <head>
          <title>Presupuesto Dental - ${patient?.nombre || ''}</title>
          <style>
            body { font-family: Arial, sans-serif; margin: 20px; }
            .header { text-align: center; margin-bottom: 20px; }
            .patient-info { margin-bottom: 20px; }
            table { width: 100%; border-collapse: collapse; margin-bottom: 20px; }
            th, td { border: 1px solid #ddd; padding: 8px; text-align: left; }
            th { background-color: #f2f2f2; }
            .totals { text-align: right; margin-top: 20px; }
            .notes { margin-top: 30px; }
          </style>
        </head>
        <body>
          <div class="header">
            <h2>Presupuesto Dental</h2>
            <p>Fecha: ${new Date(budget.fecha).toLocaleDateString('es-ES')}</p>
          </div>
          <div class="patient-info">
            <p><strong>Nombre:</strong> ${patient?.nombre || ''}</p>
            <p><strong>RUT:</strong> ${patient?.rut || ''}</p>
            <p><strong>Teléfono:</strong> ${patient?.telefono || ''}</p>
            <p><strong>Email:</strong> ${patient?.email || ''}</p>
          </div>
          <table>
            <thead>
              <tr>
                <th>Tratamiento</th>
                <th>Diente</th>
                <th>Cantidad</th>
                <th>Precio Unitario</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody>
              ${budget.items.map(item => `
                <tr>
                  <td>${item.tratamiento}</td>
                  <td>${item.diente || '-'}</td>
                  <td>${item.cantidad}</td>
                  <td>$${item.precio.toLocaleString()}</td>
                  <td>$${item.subtotal.toLocaleString()}</td>
                </tr>`).join('')}
            </tbody>
          </table>
          <div class="totals">
            <p><strong>Subtotal:</strong> $${calculateTotal().subtotal.toLocaleString()}</p>
            <p><strong>Descuento (${budget.descuento}%):</strong> $${calculateTotal().descuento.toLocaleString()}</p>
            <p><strong>Total:</strong> $${calculateTotal().total.toLocaleString()}</p>
          </div>
          ${budget.notas ? `
            <div class="notes">
              <h3>Notas:</h3>
              <p>${budget.notas}</p>
            </div>` : ''}
          <script>
            window.onload = function() {
              setTimeout(() => {
                window.print();
                window.close();
              }, 200);
            };
          </script>
        </body>
      </html>
    `);
    printWindow.document.close();
  };

  const handleConfirmBudget = async () => {
    try {
      // Primero guardar el presupuesto
      await onSaveBudget({ ...budget, estado: 'aprobado' });

      // Segundo, registrar paciente si no está en la base de datos
      const res = await fetch('http://localhost:5000/api/pacientes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          nombre: patient.nombre,
          rut: patient.rut,
          telefono: patient.telefono,
          email: patient.email
        })
      });

      const data = await res.json();
      console.log('Paciente confirmado y registrado:', data);

      // Opcional: actualizar odontograma aquí si lo necesitas

      alert('Presupuesto confirmado y paciente registrado.');
    } catch (error) {
      console.error('Error al confirmar el presupuesto:', error);
    }
  };

  return (
    <div className="bg-white rounded-xl shadow-sm p-6" ref={budgetRef}>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-xl font-semibold">Presupuesto Dental</h2>
        <div className="flex space-x-3">
          <button onClick={handlePrint} className="px-4 py-2 bg-gray-100 hover:bg-gray-200 rounded-lg">
            Imprimir
          </button>
          <button onClick={() => onSaveBudget(budget)} className="px-4 py-2 bg-blue-600 text-white rounded-lg">
            Guardar Presupuesto
          </button>
          <button onClick={handleConfirmBudget} className="px-4 py-2 bg-green-600 text-white rounded-lg">
            Confirmar Presupuesto
          </button>
        </div>
      </div>

      {/* Aquí continúa tu mismo diseño con tratamientos, notas y resumen... (omitido por espacio) */}

      {/* Puedes seguir usando lo que ya tenías para tratamientos y resumen */}
    </div>
  );
};

export default BudgetEditor;
