import React, { useEffect, useState } from 'react';
import CertificadoCompleto from './CertificadoCompleto';

const RecetasDelPaciente = ({ paciente }) => {
  const [recetas, setRecetas] = useState([]);
  const [seleccionada, setSeleccionada] = useState(null);

  useEffect(() => {
    if (paciente?._id) {
      fetch(`http://localhost:5000/api/recetas/paciente/${paciente._id}`)
        .then(res => res.json())
        .then(data => {
          console.log('üì¶ Recetas recibidas:', data);
          setRecetas(data);
        })
        .catch(err => console.error('‚ùå Error al cargar recetas', err));
    }
  }, [paciente]);

  if (!paciente) return null;

  return (
    <div className="mt-6">
      <h2 className="text-lg font-semibold mb-3">Recetas del Paciente</h2>

      {recetas.length === 0 ? (
        <p className="text-gray-500">No hay recetas registradas.</p>
      ) : (
        <div className="space-y-3">
          {recetas.map((receta) => (
            <div
              key={receta._id}
              className="p-4 bg-white rounded-lg shadow flex justify-between items-center"
            >
              <div>
                <p className="text-sm font-medium text-gray-800">
                  Fecha: {new Date(receta.fecha).toLocaleDateString()}
                </p>
                <p className="text-sm text-gray-500">
                  Medicamentos: {receta.medicamentos?.length || 0}
                </p>
              </div>
              <button
                onClick={() => setSeleccionada(receta)}
                className="text-blue-600 hover:underline text-sm"
              >
                Ver / Imprimir
              </button>
            </div>
          ))}
        </div>
      )}

      {seleccionada && (
        <div className="mt-6 border-t pt-4">
          <CertificadoCompleto receta={seleccionada} paciente={paciente} />
        </div>
      )}
    </div>
  );
};

export default RecetasDelPaciente;
