import React, { useState, useEffect } from 'react';

const FichaOrtodoncia = ({ data = {}, onUpdate }) => {
  const [form, setForm] = useState(data);
  const [selectedControl, setSelectedControl] = useState(null); // índice del control en el modal

  useEffect(() => {
    setForm(data || {});
  }, [data]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleControlChange = (index, value) => {
    const updated = [...(form.controles || [])];
    updated[index] = value;
    setForm((prev) => ({ ...prev, controles: updated }));
  };

  const handleAddControl = () => {
    setForm((prev) => ({
      ...prev,
      controles: [...(prev.controles || []), ""]
    }));
  };

  const handleSave = () => {
    if (onUpdate) {
      onUpdate(form);
      alert('✅ Ficha ortodoncia guardada correctamente');
    }
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow space-y-4">
      <h3 className="text-lg font-semibold">Ficha Ortodoncia</h3>

      {/* Datos principales */}
      {['maloclusion', 'planTratamiento', 'duracion'].map((field) => (
        <div key={field}>
          <label className="block text-sm font-medium capitalize">{field}</label>
          <input
            type="text"
            name={field}
            value={form[field] || ""}
            onChange={handleChange}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
      ))}

      {/* Lista de controles */}
      <div>
        <label className="block text-sm font-medium">Controles</label>
        <ul className="space-y-2">
          {(form.controles || []).map((ctrl, i) => (
            <li
              key={i}
              className="bg-gray-100 p-2 rounded shadow flex justify-between items-center"
            >
              <span className="text-sm">Control #{i + 1}</span>
              <button
                onClick={() => setSelectedControl(i)}
                className="text-blue-600 hover:underline text-sm"
              >
                Ver Detalles
              </button>
            </li>
          ))}
        </ul>
        <button
          className="mt-2 text-sm bg-blue-500 text-white px-3 py-1 rounded"
          onClick={handleAddControl}
        >
          Agregar control
        </button>
      </div>

      {/* Botón Guardar */}
      <div>
        <button
          className="text-sm bg-green-600 text-white px-4 py-2 rounded"
          onClick={handleSave}
        >
          Guardar cambios
        </button>
      </div>

      {/* Modal de edición de control */}
      {selectedControl !== null && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow w-full max-w-md">
            <h4 className="font-semibold mb-2">Editar Control #{selectedControl + 1}</h4>
            <textarea
              className="w-full border rounded p-2 h-32"
              value={form.controles[selectedControl]}
              onChange={(e) => handleControlChange(selectedControl, e.target.value)}
            />
            <div className="mt-4 flex justify-end">
              <button
                onClick={() => setSelectedControl(null)}
                className="px-4 py-2 bg-gray-600 text-white rounded"
              >
                Cerrar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default FichaOrtodoncia;
