import React, { useState } from 'react';
import Odontograma from './Odontograma';

const PatientDetailView = ({ patient, onUpdateOdontograma, onUpdateFichaClinica, onUpdateHistorial }) => {
  const [activeTab, setActiveTab] = useState('historial');
  const [fichaClinica, setFichaClinica] = useState(patient?.fichaClinica || {});
  const [newHistorialEntry, setNewHistorialEntry] = useState({
    fecha: new Date().toISOString().split('T')[0],
    diagnostico: '',
    tratamiento: '',
    procedimiento: '',
    observaciones: ''
  });

  // Verificar y inicializar datos del paciente
  const safePatient = {
    ...patient,
    historial: patient?.historial || [],
    fichaClinica: patient?.fichaClinica || {},
    odontograma: patient?.odontograma || Array(32).fill().map((_, i) => ({
      numero: i + 1,
      estado: 'sano',
      notas: ''
    }))
  };

  const handleFichaChange = (e) => {
    const { name, value } = e.target;
    setFichaClinica(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleHistorialChange = (e) => {
    const { name, value } = e.target;
    setNewHistorialEntry(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const addHistorialEntry = () => {
    const entryWithId = {
      ...newHistorialEntry,
      id: Date.now()
    };
    const updatedHistorial = [...safePatient.historial, entryWithId];
    onUpdateHistorial(updatedHistorial);
    setNewHistorialEntry({
      fecha: new Date().toISOString().split('T')[0],
      diagnostico: '',
      tratamiento: '',
      procedimiento: '',
      observaciones: ''
    });
  };

  const saveFichaClinica = () => {
    onUpdateFichaClinica(fichaClinica);
  };

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <div className="flex border-b border-gray-200 mb-6 overflow-x-auto">
        <button
          className={`px-4 py-2 font-medium whitespace-nowrap ${activeTab === 'historial' ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
          onClick={() => setActiveTab('historial')}
        >
          Historial Clínico
        </button>
        <button
          className={`px-4 py-2 font-medium whitespace-nowrap ${activeTab === 'ficha' ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
          onClick={() => setActiveTab('ficha')}
        >
          Ficha Ortodóntica
        </button>
        <button
          className={`px-4 py-2 font-medium whitespace-nowrap ${activeTab === 'odontograma' ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
          onClick={() => setActiveTab('odontograma')}
        >
          Odontograma
        </button>
      </div>

      {activeTab === 'historial' && (
        <div>
          <h3 className="text-lg font-semibold mb-4">Registro de Tratamientos</h3>

          <div className="mb-6 grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Fecha</label>
              <input
                type="date"
                name="fecha"
                value={newHistorialEntry.fecha}
                onChange={handleHistorialChange}
                className="w-full px-3 py-2 border border-gray-300 rounded"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Diagnóstico</label>
              <input
                type="text"
                name="diagnostico"
                value={newHistorialEntry.diagnostico}
                onChange={handleHistorialChange}
                className="w-full px-3 py-2 border border-gray-300 rounded"
                placeholder="Ej: Maloclusión Clase II"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Tratamiento</label>
              <input
                type="text"
                name="tratamiento"
                value={newHistorialEntry.tratamiento}
                onChange={handleHistorialChange}
                className="w-full px-3 py-2 border border-gray-300 rounded"
                placeholder="Ej: Brackets metálicos"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Procedimiento</label>
              <input
                type="text"
                name="procedimiento"
                value={newHistorialEntry.procedimiento}
                onChange={handleHistorialChange}
                className="w-full px-3 py-2 border border-gray-300 rounded"
                placeholder="Ej: Colocación de arco"
              />
            </div>
            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-gray-700 mb-1">Observaciones</label>
              <textarea
                name="observaciones"
                value={newHistorialEntry.observaciones}
                onChange={handleHistorialChange}
                className="w-full px-3 py-2 border border-gray-300 rounded"
                rows="2"
              />
            </div>
            <button
              onClick={addHistorialEntry}
              className="md:col-span-2 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Agregar al Historial
            </button>
          </div>

          <div className="space-y-4">
            {safePatient.historial.map((entry) => (
              <div key={entry.id} className="p-4 border border-gray-200 rounded-lg">
                <div className="flex justify-between items-start mb-2">
                  <h4 className="font-medium">{entry.fecha}</h4>
                  <span className="text-sm bg-blue-100 text-blue-800 px-2 py-1 rounded">
                    {entry.procedimiento}
                  </span>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <p className="text-sm text-gray-500">Diagnóstico</p>
                    <p>{entry.diagnostico}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Tratamiento</p>
                    <p>{entry.tratamiento}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Observaciones</p>
                    <p>{entry.observaciones}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'ficha' && (
        <div>
          <h3 className="text-lg font-semibold mb-4">Ficha Ortodóntica</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Motivo de Consulta</label>
              <textarea
                name="motivoConsulta"
                value={fichaClinica.motivoConsulta || ''}
                onChange={handleFichaChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows="3"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Historia Médica</label>
              <textarea
                name="historiaMedica"
                value={fichaClinica.historiaMedica || ''}
                onChange={handleFichaChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows="3"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Hábitos</label>
              <textarea
                name="habitos"
                value={fichaClinica.habitos || ''}
                onChange={handleFichaChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows="3"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Examen Facial</label>
              <textarea
                name="examenFacial"
                value={fichaClinica.examenFacial || ''}
                onChange={handleFichaChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows="3"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Examen Intraoral</label>
              <textarea
                name="examenIntraoral"
                value={fichaClinica.examenIntraoral || ''}
                onChange={handleFichaChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows="3"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Diagnóstico</label>
              <textarea
                name="diagnostico"
                value={fichaClinica.diagnostico || ''}
                onChange={handleFichaChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows="3"
              />
            </div>
            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-gray-700 mb-1">Plan de Tratamiento</label>
              <textarea
                name="planTratamiento"
                value={fichaClinica.planTratamiento || ''}
                onChange={handleFichaChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows="4"
              />
            </div>
          </div>
          <button
            onClick={saveFichaClinica}
            className="mt-6 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Guardar Ficha Clínica
          </button>
        </div>
      )}

      {activeTab === 'odontograma' && (
        <div>
          <h3 className="text-lg font-semibold mb-4">Odontograma Interactivo</h3>
          <Odontograma 
            data={safePatient.odontograma} 
            onUpdate={onUpdateOdontograma} 
          />
        </div>
      )}
    </div>
  );
};

export default PatientDetailView;

