import React, { useState } from 'react';

const PatientList = ({ patients, onSelectPatient }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const patientsPerPage = 10;

  // Filtrado por nombre o RUT
  const filteredPatients = patients.filter((patient) =>
    `${patient.nombre} ${patient.apellido || ''} ${patient.rut || ''}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const totalPages = Math.ceil(filteredPatients.length / patientsPerPage);
  const startIndex = (currentPage - 1) * patientsPerPage;
  const currentPatients = filteredPatients.slice(startIndex, startIndex + patientsPerPage);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    setCurrentPage(1); // Reinicia a la primera p√°gina al buscar
  };

  return (
    <div className="bg-white rounded-lg shadow p-4">
      <h3 className="text-lg font-semibold mb-4">Listado de Pacientes</h3>

      {/* üîç Input de b√∫squeda */}
      <input
        type="text"
        placeholder="Buscar por nombre o RUT..."
        value={searchTerm}
        onChange={handleSearchChange}
        className="w-full px-3 py-2 border rounded mb-4"
      />

      <ul className="divide-y divide-gray-200">
        {currentPatients.map((patient) => (
          <li
            key={patient._id || patient.id}
            className="py-2 cursor-pointer hover:bg-blue-50 px-2 rounded"
            onClick={() => onSelectPatient(patient)}
          >
            <p className="text-sm font-medium text-gray-900">
              {patient.nombre} {patient.apellido || ''} ‚Äî {patient.rut || ''}
            </p>
          </li>
        ))}
      </ul>

      {/* üîÅ Paginaci√≥n */}
      <div className="flex justify-between items-center mt-4 text-sm">
        <button
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
          className={`px-3 py-1 rounded ${currentPage === 1 ? 'bg-gray-200 text-gray-400' : 'bg-blue-100 text-blue-600 hover:bg-blue-200'}`}
        >
          Anterior
        </button>
        <span>
          P√°gina {currentPage} de {totalPages || 1}
        </span>
        <button
          disabled={currentPage === totalPages || totalPages === 0}
          onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
          className={`px-3 py-1 rounded ${currentPage === totalPages || totalPages === 0 ? 'bg-gray-200 text-gray-400' : 'bg-blue-100 text-blue-600 hover:bg-blue-200'}`}
        >
          Siguiente
        </button>
      </div>
    </div>
  );
};

export default PatientList;
