import React, { useState } from 'react';

const Login = ({ onLogin }) => {
  const [nombre, setNombre] = useState('');
  const [clave, setClave] = useState('');

  const handleLogin = async () => {
    const res = await fetch('http://localhost:5000/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ nombre, clave })
    });

    if (res.ok) {
      const data = await res.json();
      onLogin(data);
    } else {
      alert("❌ Credenciales incorrectas. Intenta nuevamente.");
    }
  };

  return (
    <div className="min-h-screen bg-indigo-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-xl shadow-lg p-8 max-w-md w-full border border-indigo-100 text-center">
        {/* Logo desde carpeta public */}
        <img
          src="/logo-rhelmu.png"
          alt="Logo RHELMÜ"
          className="w-32 mx-auto mb-4"
        />

        <h2 className="text-2xl font-bold text-indigo-800 mb-6">Iniciar Sesión</h2>

        <input
          type="text"
          placeholder="Nombre de usuario"
          value={nombre}
          onChange={e => setNombre(e.target.value)}
          className="w-full mb-3 px-4 py-2 border rounded bg-indigo-50 text-gray-800 focus:outline-none focus:ring-2 focus:ring-indigo-300"
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={clave}
          onChange={e => setClave(e.target.value)}
          className="w-full mb-6 px-4 py-2 border rounded bg-indigo-50 text-gray-800 focus:outline-none focus:ring-2 focus:ring-indigo-300"
        />

        <button
          onClick={handleLogin}
          className="w-full bg-indigo-600 text-white font-semibold py-2 rounded hover:bg-indigo-700 transition"
        >
          Ingresar
        </button>
      </div>
    </div>
  );
};

export default Login;
