import React, { useState } from 'react';

const CalendarView = ({ citas = [], pacientes = [], onAddAppointment = () => {}, onEditAppointment = () => {} }) => {
  const today = new Date();
  const [selectedDate, setSelectedDate] = useState(today);
  const [selectedAppointment, setSelectedAppointment] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [newAppointment, setNewAppointment] = useState({
    fecha: new Date().toISOString().split('T')[0],
    hora: '09:00',
    pacienteId: '',
    motivo: '',
    estado: 'pendiente'
  });

  const startOfMonth = new Date(selectedDate.getFullYear(), selectedDate.getMonth(), 1);
  const endOfMonth = new Date(selectedDate.getFullYear(), selectedDate.getMonth() + 1, 0);

  const handleAppointmentChange = (e) => {
    const { name, value } = e.target;
    setNewAppointment(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddAppointment = () => {
    const cita = { ...newAppointment, id: Date.now() };
    onAddAppointment(cita);
    setShowModal(false);
    setNewAppointment({
      fecha: new Date().toISOString().split('T')[0],
      hora: '09:00',
      pacienteId: '',
      motivo: '',
      estado: 'pendiente'
    });
  };

  const citasDelDia = (fecha) => {
    return citas.filter(c =>
      new Date(c.fecha).toDateString() === fecha.toDateString()
    );
  };

  const getPacienteNombre = (id) => {
    const paciente = pacientes.find(p => p._id === id);
    return paciente ? `${paciente.nombre} ${paciente.apellido}` : 'Paciente';
  };

  const generarDiasDelMes = () => {
    const dias = [];
    const primerDia = startOfMonth.getDay();
    const totalDias = endOfMonth.getDate();

    for (let i = 0; i < primerDia; i++) {
      dias.push(null);
    }

    for (let d = 1; d <= totalDias; d++) {
      dias.push(new Date(selectedDate.getFullYear(), selectedDate.getMonth(), d));
    }

    return dias;
  };

  return (
    <div className="bg-white rounded-xl shadow p-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-lg font-bold">Calendario Mensual</h2>
        <div className="flex items-center gap-2">
          <button onClick={() => setSelectedDate(new Date(selectedDate.setMonth(selectedDate.getMonth() - 1)))}
            className="p-2 bg-gray-100 rounded">◀</button>
          <span className="text-sm font-medium">
            {selectedDate.toLocaleDateString('es-ES', { month: 'long', year: 'numeric' })}
          </span>
          <button onClick={() => setSelectedDate(new Date(selectedDate.setMonth(selectedDate.getMonth() + 1)))}
            className="p-2 bg-gray-100 rounded">▶</button>
        </div>
        <button onClick={() => setShowModal(true)} className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700">➕ Agregar Cita</button>
      </div>

      <div className="grid grid-cols-7 gap-2 text-sm text-center">
        {['Dom', 'Lun', 'Mar', 'Mié', 'Jue', 'Vie', 'Sáb'].map(d => (
          <div key={d} className="font-semibold">{d}</div>
        ))}
        {generarDiasDelMes().map((fecha, idx) => (
          <div key={idx} className="h-24 border rounded p-1 text-left">
            {fecha && (
              <>
                <div className="text-xs font-bold">{fecha.getDate()}</div>
                {citasDelDia(fecha).slice(0, 2).map((cita, i) => (
                  <div key={i} className="text-xs truncate text-indigo-700">
                    {getPacienteNombre(cita.pacienteId)} - {cita.hora}
                  </div>
                ))}
              </>
            )}
          </div>
        ))}
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded-xl w-full max-w-md shadow-lg">
            <h3 className="text-lg font-bold mb-4">Nueva Cita</h3>
            <div className="space-y-3">
              <select name="pacienteId" value={newAppointment.pacienteId} onChange={handleAppointmentChange} className="w-full border px-3 py-2 rounded">
                <option value="">Seleccionar paciente</option>
                {pacientes.map(p => (
                  <option key={p._id} value={p._id}>{p.nombre} {p.apellido}</option>
                ))}
              </select>
              <input type="date" name="fecha" value={newAppointment.fecha} onChange={handleAppointmentChange}
                className="w-full border px-3 py-2 rounded" />
              <input type="time" name="hora" value={newAppointment.hora} onChange={handleAppointmentChange}
                className="w-full border px-3 py-2 rounded" />
              <input type="text" name="motivo" placeholder="Motivo" value={newAppointment.motivo} onChange={handleAppointmentChange}
                className="w-full border px-3 py-2 rounded" />
              <select name="estado" value={newAppointment.estado} onChange={handleAppointmentChange} className="w-full border px-3 py-2 rounded">
                <option value="pendiente">Pendiente</option>
                <option value="confirmada">Confirmada</option>
                <option value="completada">Completada</option>
                <option value="cancelada">Cancelada</option>
              </select>
              <div className="flex justify-end gap-2">
                <button onClick={() => setShowModal(false)} className="px-4 py-2 border rounded">Cancelar</button>
                <button onClick={handleAddAppointment} className="px-4 py-2 bg-indigo-600 text-white rounded">Guardar</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CalendarView;
