// src/components/ControlChecklist.js

import React, { useState } from 'react';

const ControlChecklist = ({ controles = [], onUpdate }) => {
  const [editing, setEditing] = useState(false);
  const [localControles, setLocalControles] = useState(controles);

  const toggleControl = (year, month) => {
    const updated = localControles.map(ctrl => {
      if (ctrl.year === year) {
        return {
          ...ctrl,
          months: ctrl.months.map(m =>
            m.name === month ? { ...m, asistio: !m.asistio } : m
          )
        };
      }
      return ctrl;
    });
    setLocalControles(updated);
  };

  const handleSave = () => {
    onUpdate(localControles);
    setEditing(false);
  };

  const addYear = () => {
    const nextYear = (localControles.at(-1)?.year || new Date().getFullYear()) + 1;
    const newYear = {
      year: nextYear,
      months: [
        { name: "Enero", asistio: false },
        { name: "Febrero", asistio: false },
        { name: "Marzo", asistio: false },
        { name: "Abril", asistio: false },
        { name: "Mayo", asistio: false },
        { name: "Junio", asistio: false },
        { name: "Julio", asistio: false },
        { name: "Agosto", asistio: false },
        { name: "Septiembre", asistio: false },
        { name: "Octubre", asistio: false },
        { name: "Noviembre", asistio: false },
        { name: "Diciembre", asistio: false }
      ]
    };
    setLocalControles(prev => [...prev, newYear]);
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow space-y-4">
      <div className="flex justify-between items-center">
        <h3 className="text-lg font-semibold">Historial de Controles</h3>
        <button
          onClick={() => setEditing(!editing)}
          className="text-blue-600 text-sm underline"
        >
          {editing ? "Cerrar" : "Editar"}
        </button>
      </div>

      {localControles.map(ctrl => (
        <div key={ctrl.year}>
          <h4 className="font-semibold">{ctrl.year}</h4>
          <div className="grid grid-cols-4 gap-2 mb-4">
            {ctrl.months.map(m => (
              <div
                key={m.name}
                className={`p-2 rounded-lg cursor-pointer text-center ${
                  m.asistio ? 'bg-green-200' : 'bg-red-200'
                }`}
                onClick={() => editing && toggleControl(ctrl.year, m.name)}
              >
                {m.name}
              </div>
            ))}
          </div>
        </div>
      ))}

      {editing && (
        <div className="flex space-x-2">
          <button
            onClick={addYear}
            className="px-4 py-2 bg-purple-600 text-white rounded hover:bg-purple-700"
          >
            Agregar AÃ±o
          </button>
          <button
            onClick={handleSave}
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Guardar Cambios
          </button>
        </div>
      )}
    </div>
  );
};

export default ControlChecklist;
