import React, { useState, useRef } from 'react';

const BudgetEditor = ({ patient, onSaveBudget }) => {
  const [budget, setBudget] = useState({
    id: Date.now(),
    fecha: new Date().toISOString().split('T')[0],
    pacienteId: patient?.id || patient?._id || '',
    items: [],
    descuento: 0,
    notas: '',
    estado: 'pendiente'
  });

  const [newItem, setNewItem] = useState({
    tratamiento: '',
    cantidad: 1,
    precio: 0,
    diente: ''
  });

  const budgetRef = useRef();

  const handleBudgetChange = (e) => {
    const { name, value } = e.target;
    setBudget(prev => ({ ...prev, [name]: value }));
  };

  const handleItemChange = (e) => {
    const { name, value } = e.target;
    setNewItem(prev => ({
      ...prev,
      [name]: name === 'cantidad' || name === 'precio' ? Number(value) : value
    }));
  };

  const addItem = () => {
    if (!newItem.tratamiento || newItem.precio <= 0) return;

    const itemWithId = {
      ...newItem,
      id: Date.now(),
      subtotal: newItem.cantidad * newItem.precio
    };

    setBudget(prev => ({ ...prev, items: [...prev.items, itemWithId] }));

    setNewItem({
      tratamiento: '',
      cantidad: 1,
      precio: 0,
      diente: ''
    });
  };

  const removeItem = (id) => {
    setBudget(prev => ({
      ...prev,
      items: prev.items.filter(item => item.id !== id)
    }));
  };

  const calculateTotal = () => {
    const subtotal = budget.items.reduce((sum, item) => sum + item.subtotal, 0);
    const descuento = subtotal * (budget.descuento / 100);
    return { subtotal, descuento, total: subtotal - descuento };
  };

  const handlePrint = () => {
    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <html>
        <head>
          <title>Presupuesto Dental</title>
          <style>
            body { font-family: Arial, sans-serif; padding: 20px; }
            table { width: 100%; border-collapse: collapse; margin-top: 20px; }
            th, td { border: 1px solid #ccc; padding: 8px; text-align: left; }
            th { background-color: #f5f5f5; }
          </style>
        </head>
        <body>
          <h2>Presupuesto Dental</h2>
          <p><strong>Paciente:</strong> ${patient?.nombre || ''} ${patient?.apellido || ''}</p>
          <p><strong>Fecha:</strong> ${new Date(budget.fecha).toLocaleDateString('es-ES')}</p>
          <table>
            <thead>
              <tr>
                <th>Tratamiento</th>
                <th>Diente</th>
                <th>Cantidad</th>
                <th>Precio</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody>
              ${budget.items.map(item => `
                <tr>
                  <td>${item.tratamiento}</td>
                  <td>${item.diente}</td>
                  <td>${item.cantidad}</td>
                  <td>$${item.precio.toLocaleString()}</td>
                  <td>$${item.subtotal.toLocaleString()}</td>
                </tr>
              `).join('')}
            </tbody>
          </table>
          <p><strong>Descuento:</strong> ${budget.descuento}%</p>
          <p><strong>Total:</strong> $${calculateTotal().total.toLocaleString()}</p>
        </body>
      </html>
    `);
    printWindow.document.close();
    printWindow.onload = () => {
      printWindow.print();
      printWindow.close();
    };
  };

  const handleConfirm = async () => {
    try {
      // 1. Guardar el presupuesto
      const resPresupuesto = await fetch('http://localhost:5000/api/presupuestos', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(budget)
      });

      const savedBudget = await resPresupuesto.json();

      // 2. Registrar paciente si no existe
      if (!budget.pacienteId && patient) {
        const resPaciente = await fetch('http://localhost:5000/api/pacientes', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(patient)
        });
        const savedPaciente = await resPaciente.json();

        // 3. Actualizar odontograma si hay dientes
        const dientes = budget.items
          .map(item => item.diente?.trim())
          .filter(Boolean)
          .flatMap(d => d.split(',').map(n => n.trim()));

        if (dientes.length > 0) {
          await fetch(`http://localhost:5000/api/pacientes/${savedPaciente._id}/odontograma`, {
            method: 'PATCH',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ odontograma: dientes })
          });
        }
      }

      alert('Presupuesto confirmado y paciente registrado correctamente.');
    } catch (error) {
      console.error('Error al confirmar:', error);
      alert('Hubo un error al confirmar el presupuesto.');
    }
  };

  return (
    <div className="bg-white p-6 rounded-xl shadow">
      <div className="flex justify-between mb-4">
        <h2 className="text-xl font-semibold">Presupuesto Dental</h2>
        <div className="flex gap-3">
          <button onClick={handlePrint} className="px-4 py-2 bg-gray-100 hover:bg-gray-200 rounded">
            Imprimir
          </button>
          <button onClick={() => onSaveBudget(budget)} className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
            Guardar
          </button>
          <button onClick={handleConfirm} className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700">
            Confirmar Presupuesto
          </button>
        </div>
      </div>

      <div className="grid md:grid-cols-2 gap-4 mb-6">
        <div>
          <label className="text-sm text-gray-700">Fecha</label>
          <input type="date" name="fecha" value={budget.fecha} onChange={handleBudgetChange} className="w-full border p-2 rounded" />
        </div>
        <div>
          <label className="text-sm text-gray-700">Descuento (%)</label>
          <input type="number" name="descuento" value={budget.descuento} onChange={handleBudgetChange} className="w-full border p-2 rounded" />
        </div>
      </div>

      <div className="space-y-3 mb-6">
        <h3 className="text-lg font-medium">Agregar Tratamiento</h3>
        <div className="grid md:grid-cols-4 gap-3">
          <input name="tratamiento" placeholder="Tratamiento" value={newItem.tratamiento} onChange={handleItemChange} className="border p-2 rounded" />
          <input name="diente" placeholder="Diente (ej: 11, 12)" value={newItem.diente} onChange={handleItemChange} className="border p-2 rounded" />
          <input name="cantidad" type="number" value={newItem.cantidad} onChange={handleItemChange} className="border p-2 rounded" />
          <input name="precio" type="number" value={newItem.precio} onChange={handleItemChange} className="border p-2 rounded" />
        </div>
        <button onClick={addItem} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 mt-2">Agregar Tratamiento</button>
      </div>

      {budget.items.length > 0 && (
        <div className="mb-6">
          <h4 className="text-md font-medium mb-2">Tratamientos</h4>
          <table className="min-w-full border">
            <thead className="bg-gray-50">
              <tr>
                <th className="p-2">Tratamiento</th>
                <th className="p-2">Diente</th>
                <th className="p-2">Cantidad</th>
                <th className="p-2">Precio</th>
                <th className="p-2">Subtotal</th>
                <th className="p-2">Acción</th>
              </tr>
            </thead>
            <tbody>
              {budget.items.map(item => (
                <tr key={item.id}>
                  <td className="p-2">{item.tratamiento}</td>
                  <td className="p-2">{item.diente}</td>
                  <td className="p-2">{item.cantidad}</td>
                  <td className="p-2">${item.precio.toLocaleString()}</td>
                  <td className="p-2">${item.subtotal.toLocaleString()}</td>
                  <td className="p-2">
                    <button onClick={() => removeItem(item.id)} className="text-red-500 hover:underline">Eliminar</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      <div className="bg-gray-50 p-4 rounded-lg">
        <h4 className="text-lg font-medium mb-3">Resumen</h4>
        <div className="space-y-2">
          <div className="flex justify-between">
            <span>Subtotal:</span>
            <span>${calculateTotal().subtotal.toLocaleString()}</span>
          </div>
          <div className="flex justify-between">
            <span>Descuento:</span>
            <span>-${calculateTotal().descuento.toLocaleString()}</span>
          </div>
          <div className="flex justify-between font-bold">
            <span>Total:</span>
            <span>${calculateTotal().total.toLocaleString()}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BudgetEditor;
