import React, { useEffect, useState } from 'react';
import CertificadoCompleto from './CertificadoCompleto';

const RecetasDelPaciente = ({ paciente, recargar }) => {
  const [recetas, setRecetas] = useState([]);
  const [recetaSeleccionada, setRecetaSeleccionada] = useState(null);

  const cargarRecetas = () => {
    if (paciente?._id) {
      fetch(`http://localhost:5000/api/recetas/paciente/${paciente._id}`)
        .then(res => res.json())
        .then(data => setRecetas(data));
    }
  };

  useEffect(() => {
    cargarRecetas();
  }, [paciente, recargar]); // se actualiza con `recargar`

  return (
    <div className="p-4">
      <h2 className="text-xl font-semibold mb-4">Recetas del Paciente</h2>
      {recetas.length === 0 ? (
        <p className="text-gray-500">No hay recetas registradas.</p>
      ) : (
        <ul className="space-y-2">
          {recetas.map(receta => (
            <li key={receta._id} className="border p-3 rounded shadow flex justify-between items-center">
              <div>
                <p className="font-medium">Fecha: {new Date(receta.fecha).toLocaleDateString()}</p>
                <p className="text-sm text-gray-500">Medicamentos: {receta.medicamentos.length}</p>
              </div>
              <button
                className="text-blue-600 hover:underline text-sm"
                onClick={() => setRecetaSeleccionada(receta)}
              >
                Ver / Imprimir
              </button>
            </li>
          ))}
        </ul>
      )}

      {recetaSeleccionada && (
        <div className="mt-8">
          <CertificadoCompleto receta={recetaSeleccionada} paciente={paciente} />
        </div>
      )}
    </div>
  );
};

export default RecetasDelPaciente;
