import React, { useState, useEffect } from 'react';
import LayoutHeader from './components/LayoutHeader';
import PatientList from './components/PatientList';
import PatientDetailView from './components/PatientDetailView_OdontogramaEditable';
import PatientForm from './components/PatientForm';
import CalendarView from './components/CalendarView';
import AssistantView from './components/AssistantView';
import BudgetView from './components/BudgetView';
import DashboardCard from './components/DashboardCard';
import BarChart from './components/BarChart';
import PieChart from './components/PieChart';
import WeeklyAppointments from './components/WeeklyAppointments';
import InventoryManager from './components/InventoryManager';
import ReportsView from './components/ReportsView';
import AuditLog from './components/AuditLog';
import initialData from './mock/data';
import { initializeData } from './utils/storage';

const App = () => {
  const [currentView, setCurrentView] = useState('dashboard');
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [showPatientForm, setShowPatientForm] = useState(false);
  const [editMode, setEditMode] = useState(false);

  const [pacientes, setPacientes] = useState([]);
  const [citas, setCitas] = useState([]);
  const [presupuestos, setPresupuestos] = useState([]);
  const [recetas, setRecetas] = useState([]);
  const [userRole, setUserRole] = useState('doctor');

  useEffect(() => {
    const loadedData = initializeData(initialData);
    setPacientes(loadedData.pacientes || []);
    setCitas(loadedData.citas || []);
    setPresupuestos(loadedData.presupuestos || []);
    setRecetas(loadedData.recetas || []);
    if (loadedData.pacientes.length > 0) {
      setSelectedPatient(loadedData.pacientes[0]);
    }
  }, []);

  const handleAddPatient = (data) => {
    const newPatient = {
      ...data,
      id: Date.now(),
      historial: [],
      fichaClinica: {},
      odontograma: Array.from({ length: 32 }).map((_, i) => ({
        numero: i + 1,
        estado: 'sano',
        tratamiento: '',
        notas: ''
      })),
      imagenes: [],
      aseguradora: {},
      mensajes: [],
      recordatorios: []
    };
    const updatedList = [...pacientes, newPatient];
    setPacientes(updatedList);
    setSelectedPatient(newPatient);
    setShowPatientForm(false);
    setEditMode(false);
  };

  const handleUpdatePatient = (data) => {
    const updatedList = pacientes.map(p => (p.id === data.id ? { ...p, ...data } : p));
    setPacientes(updatedList);
    setSelectedPatient(data);
    setShowPatientForm(false);
    setEditMode(false);
  };

  const handleDeletePatient = (id) => {
    const updatedList = pacientes.filter(p => p.id !== id);
    setPacientes(updatedList);
    setSelectedPatient(updatedList[0] || null);
  };

  const handleUpdateOdontograma = (updatedOdontograma) => {
    const updatedList = pacientes.map(p =>
      p.id === selectedPatient.id ? { ...p, odontograma: updatedOdontograma } : p
    );
    setPacientes(updatedList);
    setSelectedPatient(prev => ({ ...prev, odontograma: updatedOdontograma }));
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <LayoutHeader />
      <div className="max-w-7xl mx-auto px-4 py-6 grid grid-cols-12 gap-6">
        <div className="col-span-3 space-y-4">
          <div className="bg-white rounded-lg shadow p-4">
            <h2 className="font-semibold mb-4">Navegación</h2>
            <nav className="space-y-2">
              {[
                ['dashboard', 'Dashboard'],
                ['pacientes', 'Pacientes'],
                ['calendario', 'Calendario'],
                ['presupuestos', 'Presupuestos'],
                ['inventario', 'Inventario'],
                ['reportes', 'Reportes'],
                ['configuracion', 'Configuración']
              ].map(([key, label]) => (
                <button
                  key={key}
                  className={`w-full text-left px-4 py-2 rounded-lg ${currentView === key ? 'bg-blue-100 text-blue-700' : 'hover:bg-gray-100'}`}
                  onClick={() => {
                    setCurrentView(key);
                    setShowPatientForm(false);
                    setEditMode(false);
                  }}
                >
                  {label}
                </button>
              ))}
              {userRole === 'asistente' && (
                <button
                  className={`w-full text-left px-4 py-2 rounded-lg ${currentView === 'asistente' ? 'bg-blue-100 text-blue-700' : 'hover:bg-gray-100'}`}
                  onClick={() => setCurrentView('asistente')}
                >
                  Vista Asistente
                </button>
              )}
            </nav>
          </div>
        </div>

        <div className="col-span-9 space-y-6">
          {currentView === 'dashboard' && (
            <div>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <DashboardCard title="Pacientes totales" value={pacientes.length} color="blue" trend="+12%" />
                <DashboardCard title="Citas este mes" value={citas.length} color="green" trend="+5%" />
                <DashboardCard title="Presupuestos" value={presupuestos.length} color="purple" trend="+8%" />
                <DashboardCard title="Recetas" value={recetas.length} color="yellow" trend="-2%" />
              </div>
              <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mt-6">
                <div className="lg:col-span-2">
                  <BarChart data={initialData.metricas.citasPorDia} labels={['Lun', 'Mar', 'Mié', 'Jue', 'Vie', 'Sáb', 'Dom']} />
                </div>
                <div>
                  <WeeklyAppointments citas={citas} pacientes={pacientes} />
                </div>
              </div>
              <div className="grid grid-cols-1 mt-6">
                <PieChart data={initialData.metricas.especialidades} />
              </div>
            </div>
          )}

          {currentView === 'pacientes' && (
            <>
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-semibold">Pacientes</h2>
                <button
                  onClick={() => {
                    setShowPatientForm(true);
                    setEditMode(false);
                  }}
                  className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                >
                  Agregar Paciente
                </button>
              </div>

              {showPatientForm ? (
                <PatientForm
                  onSave={editMode ? handleUpdatePatient : handleAddPatient}
                  onCancel={() => {
                    setShowPatientForm(false);
                    setEditMode(false);
                  }}
                  patient={editMode ? selectedPatient : null}
                />
              ) : (
                <>
                  <PatientList patients={pacientes} onSelectPatient={setSelectedPatient} />
                  {selectedPatient && (
                    <>
                      <div className="flex gap-2 mt-4">
                        <button
                          onClick={() => {
                            setEditMode(true);
                            setShowPatientForm(true);
                          }}
                          className="bg-yellow-500 text-white px-4 py-2 rounded"
                        >
                          Editar
                        </button>
                        <button
                          onClick={() => handleDeletePatient(selectedPatient.id)}
                          className="bg-red-600 text-white px-4 py-2 rounded"
                        >
                          Eliminar
                        </button>
                      </div>
                      <PatientDetailView
                        patient={selectedPatient}
                        onUpdateOdontograma={handleUpdateOdontograma}
                      />
                    </>
                  )}
                </>
              )}
            </>
          )}

          {currentView === 'calendario' && <CalendarView citas={citas} pacientes={pacientes} />}
          {currentView === 'presupuestos' && <BudgetView presupuestos={presupuestos} pacientes={pacientes} />}
          {currentView === 'inventario' && <InventoryManager />}
          {currentView === 'reportes' && <ReportsView />}
          {currentView === 'configuracion' && <AuditLog />}
          {currentView === 'asistente' && <AssistantView />}
        </div>
      </div>
    </div>
  );
};

export default App;
