import React, { useState } from 'react';

const CalendarView = ({ citas = [], pacientes = [], onAddAppointment = () => {}, onEditAppointment = () => {} }) => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedAppointment, setSelectedAppointment] = useState(null);
  const [showAppointmentModal, setShowAppointmentModal] = useState(false);
  const [newAppointment, setNewAppointment] = useState({
    fecha: new Date().toISOString().split('T')[0],
    hora: '09:00',
    pacienteId: '',
    motivo: '',
    estado: 'pendiente'
  });
  const [viewMode, setViewMode] = useState('dia'); // dia | semana | mes

  const handleDateChange = (days) => {
    const newDate = new Date(currentDate);
    newDate.setDate(newDate.getDate() + days);
    setCurrentDate(newDate);
  };

  const handleAppointmentChange = (e) => {
    const { name, value } = e.target;
    setNewAppointment(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddAppointment = () => {
    const appointmentWithId = {
      ...newAppointment,
      id: Date.now()
    };
    onAddAppointment(appointmentWithId);
    setShowAppointmentModal(false);
    setNewAppointment({
      fecha: new Date().toISOString().split('T')[0],
      hora: '09:00',
      pacienteId: '',
      motivo: '',
      estado: 'pendiente'
    });
  };

  const handleEditAppointment = () => {
    onEditAppointment({
      ...selectedAppointment,
      ...newAppointment
    });
    setShowAppointmentModal(false);
    setSelectedAppointment(null);
  };

  const getPacienteNombre = (id) => {
    const paciente = pacientes.find(p => p._id === id);
    return paciente ? `${paciente.nombre} ${paciente.apellido}` : 'Paciente no encontrado';
  };

  const citasFiltradas = citas.filter(cita => {
    const citaFecha = new Date(cita.fecha);
    if (viewMode === 'dia') {
      return citaFecha.toDateString() === currentDate.toDateString();
    } else if (viewMode === 'semana') {
      const startOfWeek = new Date(currentDate);
      startOfWeek.setDate(currentDate.getDate() - currentDate.getDay());
      const endOfWeek = new Date(startOfWeek);
      endOfWeek.setDate(endOfWeek.getDate() + 6);
      return citaFecha >= startOfWeek && citaFecha <= endOfWeek;
    } else if (viewMode === 'mes') {
      return (
        citaFecha.getMonth() === currentDate.getMonth() &&
        citaFecha.getFullYear() === currentDate.getFullYear()
      );
    }
    return false;
  });

  return (
    <div className="bg-white rounded-xl shadow-sm p-6">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-xl font-semibold">Calendario de Citas</h2>
        <div className="flex space-x-2">
          <select
            value={viewMode}
            onChange={(e) => setViewMode(e.target.value)}
            className="px-2 py-1 border rounded text-sm"
          >
            <option value="dia">Día</option>
            <option value="semana">Semana</option>
            <option value="mes">Mes</option>
          </select>
          <button onClick={() => handleDateChange(-1)} className="p-2 rounded-lg bg-gray-100 hover:bg-gray-200">◀</button>
          <button onClick={() => setCurrentDate(new Date())} className="px-3 py-2 rounded-lg bg-gray-100 hover:bg-gray-200 text-sm font-medium">Hoy</button>
          <button onClick={() => handleDateChange(1)} className="p-2 rounded-lg bg-gray-100 hover:bg-gray-200">▶</button>
        </div>
      </div>

      <div className="mb-4 flex justify-between items-center">
        <h3 className="text-lg font-medium">
          {currentDate.toLocaleDateString('es-ES', { month: 'long', year: 'numeric' })}
        </h3>
        <button onClick={() => { setSelectedAppointment(null); setShowAppointmentModal(true); }} className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 text-sm font-medium">
          Agregar Cita
        </button>
      </div>

      <div className="space-y-3">
        {citasFiltradas.length === 0 ? (
          <p className="text-gray-500">No hay citas programadas para este {viewMode}</p>
        ) : (
          citasFiltradas.map(cita => (
            <div key={cita._id} className="p-4 border border-gray-200 rounded-lg hover:bg-gray-50 cursor-pointer" onClick={() => { setSelectedAppointment(cita); setNewAppointment(cita); setShowAppointmentModal(true); }}>
              <div className="flex justify-between items-start">
                <div>
                  <h4 className="font-medium">{getPacienteNombre(cita.pacienteId)}</h4>
                  <p className="text-sm text-gray-600">{cita.motivo}</p>
                </div>
                <div className="text-right">
                  <p className="font-medium">{cita.hora}</p>
                  <span className={`text-xs px-2 py-1 rounded-full ${
                    cita.estado === 'completada' ? 'bg-green-100 text-green-800' :
                    cita.estado === 'cancelada' ? 'bg-red-100 text-red-800' :
                    cita.estado === 'confirmada' ? 'bg-blue-100 text-blue-800' :
                    'bg-yellow-100 text-yellow-800'
                  }`}>
                    {cita.estado}
                  </span>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      {showAppointmentModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl shadow-xl p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">
              {selectedAppointment ? 'Editar Cita' : 'Nueva Cita'}
            </h3>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Paciente</label>
                <select name="pacienteId" value={newAppointment.pacienteId} onChange={handleAppointmentChange} className="w-full px-3 py-2 border border-gray-300 rounded-lg">
                  <option value="">Seleccionar paciente</option>
                  {pacientes.map(paciente => (
                    <option key={paciente._id} value={paciente._id}>{paciente.nombre} {paciente.apellido}</option>
                  ))}
                </select>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Fecha</label>
                  <input type="date" name="fecha" value={newAppointment.fecha} onChange={handleAppointmentChange} className="w-full px-3 py-2 border border-gray-300 rounded-lg" />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Hora</label>
                  <input type="time" name="hora" value={newAppointment.hora} onChange={handleAppointmentChange} className="w-full px-3 py-2 border border-gray-300 rounded-lg" />
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Motivo</label>
                <input type="text" name="motivo" value={newAppointment.motivo} onChange={handleAppointmentChange} className="w-full px-3 py-2 border border-gray-300 rounded-lg" placeholder="Ej: Control mensual" />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Estado</label>
                <select name="estado" value={newAppointment.estado} onChange={handleAppointmentChange} className="w-full px-3 py-2 border border-gray-300 rounded-lg">
                  <option value="pendiente">Pendiente</option>
                  <option value="confirmada">Confirmada</option>
                  <option value="completada">Completada</option>
                  <option value="cancelada">Cancelada</option>
                </select>
              </div>
            </div>
            <div className="mt-6 flex justify-end space-x-3">
              <button onClick={() => setShowAppointmentModal(false)} className="px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50">Cancelar</button>
              <button onClick={selectedAppointment ? handleEditAppointment : handleAddAppointment} className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700" disabled={!newAppointment.pacienteId || !newAppointment.motivo}>
                {selectedAppointment ? 'Guardar Cambios' : 'Agregar Cita'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CalendarView;
