import React, { useState, useEffect } from 'react';
import LayoutHeader from './components/LayoutHeader';
import PatientList from './components/PatientList';
import PatientDetailView from './components/PatientDetailView';
import PatientForm from './components/PatientForm';
import CalendarView from './components/CalendarView';
import AssistantView from './components/AssistantView';
import BudgetView from './components/BudgetView';
import DashboardCard from './components/DashboardCard';
import InventoryManager from './components/InventoryManager';
import ReportsView from './components/ReportsView';
import UserManager from './components/UserManager';
import WeeklyAppointments from './components/WeeklyAppointments';
import Login from './components/Login';

const App = () => {
  const [currentView, setCurrentView] = useState('dashboard');
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [showPatientForm, setShowPatientForm] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editingPatient, setEditingPatient] = useState(null);

  const [pacientes, setPacientes] = useState([]);
  const [citas, setCitas] = useState([]);
  const [presupuestos, setPresupuestos] = useState([]);
  const [recetas, setRecetas] = useState([]);

  const [usuario, setUsuario] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem('usuario');
    if (storedUser) {
      setUsuario(JSON.parse(storedUser));
    }
  }, []);

  const handleLogin = (user) => {
    localStorage.setItem('usuario', JSON.stringify(user));
    setUsuario(user);
  };

  const handleLogout = () => {
    localStorage.removeItem('usuario');
    setUsuario(null);
  };

  useEffect(() => {
    if (!usuario) return;
    fetch("http://localhost:5000/api/pacientes")
      .then(res => res.json())
      .then(data => {
        setPacientes(data);
        if (data.length > 0) setSelectedPatient(data[0]);
      });
  }, [usuario]);

  useEffect(() => {
    fetch('http://localhost:5000/api/citas')
      .then(res => res.json())
      .then(data => setCitas(data));
  }, []);

  useEffect(() => {
    fetch('http://localhost:5000/api/recetas')
      .then(res => res.json())
      .then(data => setRecetas(data));
  }, []);

  if (!usuario) return <Login onLogin={handleLogin} />;

  const isAdmin = usuario.rol === 'admin' || usuario.rol === 'doctor' || usuario.rol === 'contador';
  const isAsistente = usuario.rol === 'asistente';

  return (
    <div className="min-h-screen bg-indigo-50">
      <LayoutHeader usuario={usuario} onLogout={handleLogout} />

      <div className="max-w-7xl mx-auto px-4 py-6 grid grid-cols-12 gap-6">
        <div className="col-span-3 space-y-4">
          <div className="bg-white rounded-2xl shadow-md p-5 border border-indigo-100">
            <h2 className="font-bold mb-4 text-indigo-800 uppercase tracking-wide text-sm">Men√∫ Principal</h2>
            <nav className="space-y-2 text-sm">
              <button onClick={() => setCurrentView('dashboard')} className="w-full text-left px-4 py-2 hover:bg-indigo-100">üìä Dashboard</button>
              <button onClick={() => setCurrentView('pacientes')} className="w-full text-left px-4 py-2 hover:bg-indigo-100">üßë‚Äç‚öïÔ∏è Pacientes</button>
              <button onClick={() => setCurrentView('calendario')} className="w-full text-left px-4 py-2 hover:bg-indigo-100">üìÖ Calendario</button>
              {isAdmin && (
                <>
                  <button onClick={() => setCurrentView('presupuestos')} className="w-full text-left px-4 py-2 hover:bg-indigo-100">üí∞ Presupuestos</button>
                  <button onClick={() => setCurrentView('inventario')} className="w-full text-left px-4 py-2 hover:bg-indigo-100">üì¶ Inventario</button>
                  <button onClick={() => setCurrentView('reportes')} className="w-full text-left px-4 py-2 hover:bg-indigo-100">üìà Reportes</button>
                  <button onClick={() => setCurrentView('configuracion')} className="w-full text-left px-4 py-2 hover:bg-indigo-100">‚öôÔ∏è Configuraci√≥n</button>
                </>
              )}
              {isAsistente && (
                <button onClick={() => setCurrentView('asistente')} className="w-full text-left px-4 py-2 hover:bg-indigo-100">üßë‚Äçüíº Vista Asistente</button>
              )}
            </nav>
          </div>
        </div>

        <div className="col-span-9 space-y-6">
          {currentView === 'dashboard' && (
            <>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <DashboardCard title="Pacientes" value={pacientes.length} color="indigo" />
                <DashboardCard title="Citas" value={citas.length} color="green" />
                <DashboardCard title="Presupuestos" value={presupuestos.length} color="purple" />
                <DashboardCard title="Recetas" value={recetas.length} color="yellow" />
              </div>
              <WeeklyAppointments citas={citas} pacientes={pacientes} />
            </>
          )}

          {currentView === 'pacientes' && (
            <>
              <PatientList patients={pacientes} onSelectPatient={setSelectedPatient} />
              {selectedPatient && (
                <PatientDetailView patient={selectedPatient} />
              )}
              {!isAsistente && showPatientForm && (
                <PatientForm
                  onSave={(p) => {
                    setPacientes(prev => [...prev, p]);
                    setSelectedPatient(p);
                    setShowPatientForm(false);
                  }}
                  onCancel={() => setShowPatientForm(false)}
                />
              )}
              {!isAsistente && (
                <button onClick={() => setShowPatientForm(true)} className="bg-indigo-600 text-white px-4 py-2 rounded">
                  ‚ûï Agregar Paciente
                </button>
              )}
            </>
          )}

          {currentView === 'calendario' && (
            <CalendarView
              citas={citas}
              pacientes={pacientes}
              onAddAppointment={(c) => setCitas(prev => [...prev, c])}
              onEditAppointment={(updated) =>
                setCitas(prev => prev.map(c => (c._id === updated._id ? updated : c)))
              }
              readOnly={isAsistente}
            />
          )}

          {currentView === 'presupuestos' && isAdmin && <BudgetView presupuestos={presupuestos} pacientes={pacientes} />}
          {currentView === 'inventario' && isAdmin && <InventoryManager />}
          {currentView === 'reportes' && isAdmin && <ReportsView />}
          {currentView === 'configuracion' && isAdmin && <UserManager />}
          {currentView === 'asistente' && isAsistente && <AssistantView />}
        </div>
      </div>
    </div>
  );
};

export default App;
