import React, { useState } from 'react';

const MedicalRecord = ({ patient }) => {
  const [activeTab, setActiveTab] = useState('historial');

  const historial = patient?.historialClinico;

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <div className="flex border-b border-gray-200 mb-6">
        <button
          className={`px-4 py-2 font-medium ${activeTab === 'historial' ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
          onClick={() => setActiveTab('historial')}
        >
          Historial
        </button>
        <button
          className={`px-4 py-2 font-medium ${activeTab === 'odontograma' ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
          onClick={() => setActiveTab('odontograma')}
        >
          Odontograma
        </button>
      </div>

      {activeTab === 'historial' && (
        <div>
          <h3 className="text-lg font-semibold mb-4">Historial Clínico</h3>
          {Array.isArray(historial) && historial.length > 0 ? (
            historial.map((item, index) => (
              <div key={index} className="mb-4 p-4 bg-gray-50 rounded-lg">
                <p className="text-sm text-gray-500">{item.fecha}</p>
                <p className="font-medium">{item.diagnostico}</p>
                <p>{item.tratamiento}</p>
              </div>
            ))
          ) : (
            <p className="text-gray-500">Sin registros en el historial clínico.</p>
          )}
        </div>
      )}

      {activeTab === 'odontograma' && (
        <div>
          <h3 className="text-lg font-semibold mb-4">Odontograma</h3>
          <div className="grid grid-cols-8 gap-2">
            {Array.from({ length: 32 }).map((_, i) => (
              <div key={i} className="h-10 bg-gray-100 rounded flex items-center justify-center">
                {i + 1}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default MedicalRecord;
