import React, { useState } from 'react';

const RecipeEditor = ({ patient, onSaveRecipe }) => {
  const [recipe, setRecipe] = useState({
    id: Date.now(),
    fecha: new Date().toISOString().split('T')[0],
    pacienteId: patient?.id || '',
    medicamentos: [],
    indicaciones: '',
    profesional: '',
    vigencia: 30
  });

  const [newMed, setNewMed] = useState({
    nombre: '',
    dosis: '',
    frecuencia: '',
    duracion: ''
  });

  const handleRecipeChange = (e) => {
    const { name, value } = e.target;
    setRecipe(prev => ({ ...prev, [name]: value }));
  };

  const handleMedChange = (e) => {
    const { name, value } = e.target;
    setNewMed(prev => ({ ...prev, [name]: value }));
  };

  const addMedication = () => {
    if (!newMed.nombre) return;
    setRecipe(prev => ({
      ...prev,
      medicamentos: [...prev.medicamentos, { ...newMed, id: Date.now() }]
    }));
    setNewMed({
      nombre: '',
      dosis: '',
      frecuencia: '',
      duracion: ''
    });
  };

  const removeMedication = (id) => {
    setRecipe(prev => ({
      ...prev,
      medicamentos: prev.medicamentos.filter(m => m.id !== id)
    }));
  };

  return (
    <div className="bg-white rounded-xl shadow-sm p-6">
      <h2 className="text-xl font-semibold mb-6">Receta Médica</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Fecha</label>
          <input
            type="date"
            name="fecha"
            value={recipe.fecha}
            onChange={handleRecipeChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Vigencia (días)</label>
          <input
            type="number"
            name="vigencia"
            value={recipe.vigencia}
            onChange={handleRecipeChange}
            min="1"
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
          />
        </div>
      </div>

      <div className="mb-6">
        <h3 className="text-lg font-medium mb-3">Medicamentos</h3>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-3 mb-3">
          <div>
            <input
              type="text"
              name="nombre"
              value={newMed.nombre}
              onChange={handleMedChange}
              placeholder="Nombre"
              className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            />
          </div>
          <div>
            <input
              type="text"
              name="dosis"
              value={newMed.dosis}
              onChange={handleMedChange}
              placeholder="Dosis"
              className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            />
          </div>
          <div>
            <input
              type="text"
              name="frecuencia"
              value={newMed.frecuencia}
              onChange={handleMedChange}
              placeholder="Frecuencia"
              className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            />
          </div>
          <div>
            <input
              type="text"
              name="duracion"
              value={newMed.duracion}
              onChange={handleMedChange}
              placeholder="Duración"
              className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            />
          </div>
        </div>
        <button
          onClick={addMedication}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          disabled={!newMed.nombre}
        >
          Agregar Medicamento
        </button>
      </div>

      {recipe.medicamentos.length > 0 && (
        <div className="mb-6">
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Medicamento</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Dosis</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Frecuencia</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Duración</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Acciones</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {recipe.medicamentos.map(med => (
                  <tr key={med.id}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{med.nombre}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{med.dosis}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{med.frecuencia}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{med.duracion}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      <button
                        onClick={() => removeMedication(med.id)}
                        className="text-red-600 hover:text-red-900"
                      >
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      <div className="mb-6">
        <label className="block text-sm font-medium text-gray-700 mb-1">Indicaciones adicionales</label>
        <textarea
          name="indicaciones"
          value={recipe.indicaciones}
          onChange={handleRecipeChange}
          rows="3"
          className="w-full px-3 py-2 border border-gray-300 rounded-lg"
          placeholder="Indicaciones para el paciente..."
        />
      </div>

      <div className="flex justify-end space-x-3">
        <button
          onClick={() => onSaveRecipe(recipe)}
          className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
        >
          Guardar Receta
        </button>
      </div>
    </div>
  );
};

export default RecipeEditor;
