import React, { useState, useEffect } from 'react';

const monthNames = [
  'Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio',
  'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'
];

const ControlChecklist = ({ controles = [], onUpdate }) => {
  const [localControles, setLocalControles] = useState([]);
  const currentYear = new Date().getFullYear();

  useEffect(() => {
    let updatedControles = [...controles];
    if (!controles.find(c => c.year === currentYear)) {
      updatedControles.push({
        year: currentYear,
        months: monthNames.map(m => ({ month: m, attended: false }))
      });
      onUpdate(updatedControles);
    }
    setLocalControles(updatedControles);
  }, [controles, onUpdate, currentYear]);

  const toggleAttendance = (year, month) => {
    const updated = localControles.map(control => {
      if (control.year === year) {
        return {
          ...control,
          months: control.months.map(m => m.month === month ? { ...m, attended: !m.attended } : m)
        };
      }
      return control;
    });
    setLocalControles(updated);
    onUpdate(updated);
    alert('✅ Control actualizado con éxito');
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow">
      <h3 className="text-lg font-semibold mb-4">Historial de Controles</h3>
      {localControles.filter(c => c.year === currentYear).map(control => (
        <div key={control.year}>
          <h4 className="text-md font-semibold mb-2">Año {control.year}</h4>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            {control.months.map(m => (
              <div key={m.month} className="flex items-center space-x-2">
                <span className="text-sm w-20">{m.month}</span>
                <button
                  className={`px-2 py-1 text-xs rounded ${m.attended ? 'bg-green-500 text-white' : 'bg-red-500 text-white'}`}
                  onClick={() => toggleAttendance(control.year, m.month)}
                >
                  {m.attended ? 'Asistió' : 'No asistió'}
                </button>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ControlChecklist;
