import React, { useState, useEffect } from 'react';
import RecetasDelPaciente from './RecetasDelPaciente';

const RecipeEditor = ({ patient }) => {
  const [recipe, setRecipe] = useState({
    id: Date.now(),
    fecha: new Date().toISOString().split('T')[0],
    pacienteId: patient?._id || '',
    medicamentos: [],
    indicaciones: '',
    profesional: 'Dr. Mauricio Hurtado Cáceres',
    vigencia: 30
  });

  const [newMed, setNewMed] = useState({
    nombre: '',
    dosis: '',
    frecuencia: '',
    duracion: ''
  });

  const [recetaGuardada, setRecetaGuardada] = useState(null);

  const handleRecipeChange = (e) => {
    const { name, value } = e.target;
    setRecipe(prev => ({ ...prev, [name]: value }));
  };

  const handleMedChange = (e) => {
    const { name, value } = e.target;
    setNewMed(prev => ({ ...prev, [name]: value }));
  };

  const addMedication = () => {
    if (!newMed.nombre) return;
    setRecipe(prev => ({
      ...prev,
      medicamentos: [...prev.medicamentos, { ...newMed, id: Date.now() }]
    }));
    setNewMed({ nombre: '', dosis: '', frecuencia: '', duracion: '' });
  };

  const removeMedication = (id) => {
    setRecipe(prev => ({
      ...prev,
      medicamentos: prev.medicamentos.filter(m => m.id !== id)
    }));
  };

  const handleGuardar = () => {
    if (!patient?._id) return alert("Paciente no válido");
    const recetaParaGuardar = { ...recipe, pacienteId: patient._id };
    fetch('http://localhost:5000/api/recetas', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(recetaParaGuardar)
    })
      .then(res => res.json())
      .then(data => {
        setRecetaGuardada(data);
        resetFormulario();
      })
      .catch(() => alert('Error al guardar receta'));
  };

  const resetFormulario = () => {
    setRecipe({
      id: Date.now(),
      fecha: new Date().toISOString().split('T')[0],
      pacienteId: patient?._id || '',
      medicamentos: [],
      indicaciones: '',
      profesional: 'Dr. Mauricio Hurtado Cáceres',
      vigencia: 30
    });
  };

  return (
    <div className="bg-white rounded-xl shadow-sm p-6">
      <h2 className="text-xl font-semibold mb-6">Receta Médica</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Fecha</label>
          <input
            type="date"
            name="fecha"
            value={recipe.fecha}
            onChange={handleRecipeChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Vigencia (días)</label>
          <input
            type="number"
            name="vigencia"
            value={recipe.vigencia}
            onChange={handleRecipeChange}
            min="1"
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
          />
        </div>
      </div>

      <div className="mb-6">
        <h3 className="text-lg font-medium mb-3">Medicamentos</h3>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-3 mb-3">
          {['nombre', 'dosis', 'frecuencia', 'duracion'].map(field => (
            <input
              key={field}
              type="text"
              name={field}
              value={newMed[field]}
              onChange={handleMedChange}
              placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            />
          ))}
        </div>
        <button
          onClick={addMedication}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          disabled={!newMed.nombre}
        >
          Agregar Medicamento
        </button>
      </div>

      {recipe.medicamentos.length > 0 && (
        <div className="mb-6 overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                {['Medicamento', 'Dosis', 'Frecuencia', 'Duración', 'Acciones'].map(header => (
                  <th key={header} className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">{header}</th>
                ))}
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {recipe.medicamentos.map(med => (
                <tr key={med.id}>
                  <td className="px-6 py-4 text-sm">{med.nombre}</td>
                  <td className="px-6 py-4 text-sm">{med.dosis}</td>
                  <td className="px-6 py-4 text-sm">{med.frecuencia}</td>
                  <td className="px-6 py-4 text-sm">{med.duracion}</td>
                  <td className="px-6 py-4 text-sm">
                    <button onClick={() => removeMedication(med.id)} className="text-red-600 hover:text-red-900">Eliminar</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      <div className="mb-6">
        <label className="block text-sm font-medium text-gray-700 mb-1">Indicaciones adicionales</label>
        <textarea
          name="indicaciones"
          value={recipe.indicaciones}
          onChange={handleRecipeChange}
          rows="3"
          className="w-full px-3 py-2 border border-gray-300 rounded-lg"
          placeholder="Indicaciones para el paciente..."
        />
      </div>

      <div className="flex justify-end space-x-3">
        <button
          onClick={handleGuardar}
          className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
        >
          Guardar Receta
        </button>
      </div>

      <div className="mt-10">
        <RecetasDelPaciente paciente={patient} />
      </div>
    </div>
  );
};

export default RecipeEditor;
