import React, { useState, useEffect } from 'react';

const Odontograma = ({ data = [], onUpdate }) => {
  const [odontograma, setOdontograma] = useState([]);
  const [activeTooth, setActiveTooth] = useState(null);
  const [formData, setFormData] = useState({
    estado: 'sano',
    tratamiento: '',
    notas: ''
  });

  const toothStates = [
    { value: 'sano', label: 'Sano', color: 'bg-white' },
    { value: 'caries', label: 'Caries', color: 'bg-yellow-300' },
    { value: 'obturado', label: 'Obturado', color: 'bg-blue-300' },
    { value: 'extraido', label: 'ExtraÃ­do', color: 'bg-gray-300' },
    { value: 'endodoncia', label: 'Endodoncia', color: 'bg-purple-300' },
    { value: 'corona', label: 'Corona', color: 'bg-green-300' },
    { value: 'implante', label: 'Implante', color: 'bg-red-300' }
  ];

  const toFDI = (numero) => {
    if (numero >= 1 && numero <= 8) return `1.${9 - numero}`;
    if (numero >= 9 && numero <= 16) return `2.${numero - 8}`;
    if (numero >= 17 && numero <= 24) return `4.${25 - numero}`;
    if (numero >= 25 && numero <= 32) return `3.${numero - 24}`;
    return numero;
  };

  useEffect(() => {
    const inicial = Array.from({ length: 32 }).map((_, i) => {
      return data.find(t => t.numero === i + 1) || {
        numero: i + 1,
        estado: 'sano',
        tratamiento: '',
        notas: ''
      };
    });
    setOdontograma(inicial);
  }, [data]);

  const handleToothClick = (tooth) => {
    setActiveTooth(tooth);
    setFormData({
      estado: tooth.estado,
      tratamiento: tooth.tratamiento,
      notas: tooth.notas
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const saveChanges = () => {
    const updated = odontograma.map((tooth) =>
      tooth.numero === activeTooth.numero ? { ...tooth, ...formData } : tooth
    );
    setOdontograma(updated);
    onUpdate(updated);
    setActiveTooth(null);
  };

  const getToothColor = (state) => {
    const stateObj = toothStates.find((s) => s.value === state);
    return stateObj ? stateObj.color : 'bg-white';
  };

  return (
    <div>
      <h3 className="text-lg font-semibold mb-4">Odontograma (FDI)</h3>

      <div className="grid grid-cols-8 gap-2 mb-4">
        {odontograma.slice(0, 8).reverse().map((tooth, i) => (
          <div
            key={`upper-right-${i}`}
            onClick={() => handleToothClick(tooth)}
            className={`h-10 border border-gray-300 rounded-t-full flex items-center justify-center cursor-pointer transition-colors ${getToothColor(tooth.estado)} ${activeTooth?.numero === tooth.numero ? 'ring-2 ring-blue-500' : ''}`}
            title={toFDI(tooth.numero)}
          >
            {toFDI(tooth.numero)}
          </div>
        ))}
        {odontograma.slice(8, 16).map((tooth, i) => (
          <div
            key={`upper-left-${i}`}
            onClick={() => handleToothClick(tooth)}
            className={`h-10 border border-gray-300 rounded-t-full flex items-center justify-center cursor-pointer transition-colors ${getToothColor(tooth.estado)} ${activeTooth?.numero === tooth.numero ? 'ring-2 ring-blue-500' : ''}`}
            title={toFDI(tooth.numero)}
          >
            {toFDI(tooth.numero)}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-8 gap-2 mb-6">
        {odontograma.slice(16, 24).reverse().map((tooth, i) => (
          <div
            key={`lower-right-${i}`}
            onClick={() => handleToothClick(tooth)}
            className={`h-10 border border-gray-300 rounded-b-full flex items-center justify-center cursor-pointer transition-colors ${getToothColor(tooth.estado)} ${activeTooth?.numero === tooth.numero ? 'ring-2 ring-blue-500' : ''}`}
            title={toFDI(tooth.numero)}
          >
            {toFDI(tooth.numero)}
          </div>
        ))}
        {odontograma.slice(24, 32).map((tooth, i) => (
          <div
            key={`lower-left-${i}`}
            onClick={() => handleToothClick(tooth)}
            className={`h-10 border border-gray-300 rounded-b-full flex items-center justify-center cursor-pointer transition-colors ${getToothColor(tooth.estado)} ${activeTooth?.numero === tooth.numero ? 'ring-2 ring-blue-500' : ''}`}
            title={toFDI(tooth.numero)}
          >
            {toFDI(tooth.numero)}
          </div>
        ))}
      </div>

      {activeTooth && (
        <div className="bg-gray-50 p-4 rounded-lg shadow">
          <h4 className="font-medium mb-4">Editar Pieza {toFDI(activeTooth.numero)}</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
            <div>
              <label className="block text-sm font-medium mb-1">Estado</label>
              <select
                name="estado"
                value={formData.estado}
                onChange={handleChange}
                className="w-full border px-3 py-2 rounded"
              >
                {toothStates.map((s) => (
                  <option key={s.value} value={s.value}>
                    {s.label}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium mb-1">Tratamiento</label>
              <input
                name="tratamiento"
                value={formData.tratamiento}
                onChange={handleChange}
                className="w-full border px-3 py-2 rounded"
                placeholder="Ej: ObturaciÃ³n, Endodoncia..."
              />
            </div>
          </div>
          <div className="mb-4">
            <label className="block text-sm font-medium mb-1">Notas</label>
            <textarea
              name="notas"
              value={formData.notas}
              onChange={handleChange}
              className="w-full border px-3 py-2 rounded"
              rows="3"
              placeholder="Detalles adicionales..."
            />
          </div>
          <div className="flex justify-end space-x-2">
            <button
              onClick={() => setActiveTooth(null)}
              className="px-4 py-2 border rounded text-gray-700 hover:bg-gray-100"
            >
              Cancelar
            </button>
            <button
              onClick={saveChanges}
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Guardar Cambios
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Odontograma;
