import React from 'react';

const CertificadoCompleto = ({ receta, paciente }) => {
  const fecha = new Date(receta.fecha).toLocaleDateString();

  const handlePrint = () => {
    const contenido = document.getElementById(`cert-${receta._id}`).innerHTML;

    const ventana = window.open('', '', 'width=800,height=1000');
    ventana.document.write(`
      <html>
        <head>
          <title>Imprimir Certificado</title>
          <style>
            body {
              font-family: Arial, sans-serif;
              padding: 40px;
              color: #000;
            }
            .center { text-align: center; }
            .bold { font-weight: bold; }
            .logo { height: 70px; margin-bottom: 10px; }
            .linea { border-top: 1px solid #000; width: 300px; margin: 20px auto 5px; }
            .small { font-size: 12px; text-align: center; margin-top: 20px; }
            .marca-agua {
              position: fixed;
              top: 50%;
              left: 50%;
              transform: translate(-50%, -50%);
              font-size: 80px;
              font-weight: bold;
              color: rgba(0, 170, 255, 0.06);
              pointer-events: none;
              z-index: 0;
              text-align: center;
              white-space: nowrap;
            }
          </style>
        </head>
        <body>
          <div class="marca-agua">ORTODONCIA RHELMÜ</div>
          ${contenido}
        </body>
      </html>
    `);
    ventana.document.close();
    ventana.focus();
    setTimeout(() => {
      ventana.print();
      ventana.close();
    }, 500); // Espera para que cargue el logo
  };

  return (
    <div className="p-4">
      <button
        onClick={handlePrint}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 mb-4"
      >
        Imprimir Certificado
      </button>

      <div id={`cert-${receta._id}`} className="max-w-[800px] mx-auto p-10 border bg-white text-black relative">
        <div className="text-center mb-6 relative z-10">
          <img src="/logo-rhelmu.png" alt="Logo" className="logo mx-auto" />
          <h2 className="text-xl font-semibold">Dr. Mauricio Hurtado Cáceres</h2>
          <p className="text-blue-600 font-semibold">Ortodoncia y Ortopedia</p>
          <p className="text-blue-500">Dentomaxilofacial</p>
          <p className="text-sm mt-1">Rut: 12.917.335-1</p>
        </div>

        <h3 className="text-center text-lg font-bold underline mb-4 relative z-10">CERTIFICADO</h3>

        <div className="mb-6 space-y-1 text-sm relative z-10">
          <p><strong>NOMBRE:</strong> {paciente.nombre}</p>
          <p><strong>RUT:</strong> {paciente.rut}</p>
          <p><strong>FECHA:</strong> {fecha}</p>
        </div>

        <div className="marca-agua">RHELMÜ</div>

        <div className="center mt-12 relative z-10">
          <div className="linea"></div>
          <p className="bold">Dr. Mauricio Hurtado Cáceres</p>
          <p className="text-sm">12.917.335-1</p>
        </div>

        <div className="small relative z-10">
          Dirección: Lord Cochrane 635, Torre B, piso 10, Oficina 1002, Concepción.<br />
          Teléfono: +56 944435569
        </div>
      </div>
    </div>
  );
};

export default CertificadoCompleto;
