import React, { useState } from 'react';

const horasDelDia = Array.from({ length: 10 }, (_, i) => `${9 + i}:00`);

const CalendarView = ({ citas = [], pacientes = [], onAddAppointment = () => {}, onEditAppointment = () => {} }) => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedAppointment, setSelectedAppointment] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [newAppointment, setNewAppointment] = useState({
    fecha: new Date().toISOString().split('T')[0],
    hora: '09:00',
    pacienteId: '',
    motivo: '',
    estado: 'pendiente'
  });

  const startOfWeek = new Date(currentDate);
  startOfWeek.setDate(currentDate.getDate() - currentDate.getDay() + 1); // Lunes

  const daysOfWeek = Array.from({ length: 7 }, (_, i) => {
    const d = new Date(startOfWeek);
    d.setDate(d.getDate() + i);
    return d;
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewAppointment(prev => ({ ...prev, [name]: value }));
  };

  const handleAdd = () => {
    onAddAppointment({ ...newAppointment, id: Date.now() });
    setShowModal(false);
    setNewAppointment({ fecha: new Date().toISOString().split('T')[0], hora: '09:00', pacienteId: '', motivo: '', estado: 'pendiente' });
  };

  const citasSemana = citas.filter(cita => {
    const date = new Date(cita.fecha);
    return date >= daysOfWeek[0] && date <= daysOfWeek[6];
  });

  const getPacienteNombre = (id) => {
    const p = pacientes.find(p => p._id === id);
    return p ? `${p.nombre} ${p.apellido}` : '';
  };

  const renderCitasEnCelda = (fecha, hora) => {
    return citasSemana
      .filter(c => c.fecha === fecha && c.hora === hora)
      .map((cita, i) => (
        <div key={i}
             className="bg-blue-100 text-blue-800 text-xs p-1 rounded cursor-pointer hover:bg-blue-200"
             onClick={() => { setSelectedAppointment(cita); setNewAppointment(cita); setShowModal(true); }}>
          <strong>{getPacienteNombre(cita.pacienteId)}</strong><br />
          <span className="capitalize">{cita.estado}</span>
        </div>
      ));
  };

  return (
    <div className="p-6 bg-white shadow rounded-xl">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-semibold text-indigo-800">Agenda Semanal</h2>
        <div className="flex gap-2">
          <button onClick={() => setCurrentDate(new Date(currentDate.setDate(currentDate.getDate() - 7)))}
                  className="px-2 py-1 bg-gray-100 rounded hover:bg-gray-200">◀</button>
          <button onClick={() => setCurrentDate(new Date())}
                  className="px-3 py-1 bg-gray-100 rounded hover:bg-gray-200">Hoy</button>
          <button onClick={() => setCurrentDate(new Date(currentDate.setDate(currentDate.getDate() + 7)))}
                  className="px-2 py-1 bg-gray-100 rounded hover:bg-gray-200">▶</button>
          <button onClick={() => { setShowModal(true); setSelectedAppointment(null); }}
                  className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 text-sm">+ Cita</button>
        </div>
      </div>

      <div className="overflow-x-auto">
        <table className="min-w-full text-xs border border-gray-200">
          <thead>
            <tr>
              <th className="bg-indigo-50 text-left p-2 border border-gray-200 w-20">Hora</th>
              {daysOfWeek.map((d, i) => (
                <th key={i} className="bg-indigo-50 text-center p-2 border border-gray-200">
                  {d.toLocaleDateString('es-ES', { weekday: 'short', day: 'numeric' })}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {horasDelDia.map((hora, i) => (
              <tr key={i}>
                <td className="text-center p-2 border border-gray-200 bg-gray-50 font-medium">{hora}</td>
                {daysOfWeek.map((d, j) => (
                  <td key={j} className="p-1 border border-gray-200 h-16 align-top">
                    {renderCitasEnCelda(d.toISOString().split('T')[0], hora)}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">{selectedAppointment ? 'Editar' : 'Nueva'} Cita</h3>
            <div className="space-y-3">
              <select name="pacienteId" value={newAppointment.pacienteId} onChange={handleChange}
                      className="w-full p-2 border border-gray-300 rounded">
                <option value="">Seleccionar paciente</option>
                {pacientes.map(p => (
                  <option key={p._id} value={p._id}>{p.nombre} {p.apellido}</option>
                ))}
              </select>
              <input type="date" name="fecha" value={newAppointment.fecha} onChange={handleChange}
                     className="w-full p-2 border border-gray-300 rounded" />
              <input type="time" name="hora" value={newAppointment.hora} onChange={handleChange}
                     className="w-full p-2 border border-gray-300 rounded" />
              <input type="text" name="motivo" value={newAppointment.motivo} onChange={handleChange}
                     className="w-full p-2 border border-gray-300 rounded" placeholder="Motivo" />
              <select name="estado" value={newAppointment.estado} onChange={handleChange}
                      className="w-full p-2 border border-gray-300 rounded">
                <option value="pendiente">Pendiente</option>
                <option value="confirmada">Confirmada</option>
                <option value="completada">Completada</option>
                <option value="cancelada">Cancelada</option>
              </select>
              <div className="flex justify-end gap-2 pt-4">
                <button onClick={() => setShowModal(false)} className="px-4 py-2 bg-gray-200 rounded hover:bg-gray-300">Cancelar</button>
                <button onClick={selectedAppointment ? () => onEditAppointment(newAppointment) : handleAdd}
                        className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
                  {selectedAppointment ? 'Guardar' : 'Agregar'}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CalendarView;
