import React, { useState } from 'react';

const CalendarView = ({ citas = [], pacientes = [], onAddAppointment = () => {}, onEditAppointment = () => {} }) => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [showModal, setShowModal] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [newAppointment, setNewAppointment] = useState({
    fecha: '',
    hora: '09:00',
    pacienteId: '',
    motivo: '',
    estado: 'pendiente'
  });

  const startOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
  const endOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0);
  const startDay = startOfMonth.getDay();
  const daysInMonth = endOfMonth.getDate();

  const getCitasForDate = (fecha) => {
    return citas.filter(cita => new Date(cita.fecha).toDateString() === fecha.toDateString());
  };

  const getPacienteNombre = (id) => {
    const paciente = pacientes.find(p => p._id === id);
    return paciente ? `${paciente.nombre} ${paciente.apellido}` : 'Paciente';
  };

  const handleOpenModal = (fecha) => {
    setSelectedDate(fecha);
    setNewAppointment({
      fecha: fecha.toISOString().split('T')[0], // solo YYYY-MM-DD
      hora: '09:00',
      pacienteId: '',
      motivo: '',
      estado: 'pendiente'
    });    
    setShowModal(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewAppointment(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    onAddAppointment({ ...newAppointment });
    setShowModal(false);
  };

  const goToPrevMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1, 1));
  };

  const goToNextMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 1));
  };

  const today = new Date();

  return (
    <div className="bg-white p-6 rounded-xl shadow-md">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-semibold text-indigo-800">Calendario de Citas</h2>
        <div className="flex space-x-2">
          <button onClick={goToPrevMonth} className="bg-gray-100 px-2 py-1 rounded">←</button>
          <span className="text-indigo-700 font-medium">
            {currentDate.toLocaleDateString('es-ES', { month: 'long', year: 'numeric' })}
          </span>
          <button onClick={goToNextMonth} className="bg-gray-100 px-2 py-1 rounded">→</button>
        </div>
      </div>

      <div className="grid grid-cols-7 text-sm text-center mb-2 font-medium text-indigo-600">
        {['Dom', 'Lun', 'Mar', 'Mié', 'Jue', 'Vie', 'Sáb'].map((d) => <div key={d}>{d}</div>)}
      </div>

      <div className="grid grid-cols-7 gap-1">
        {[...Array(startDay).fill(null), ...Array(daysInMonth).fill(0).map((_, i) => i + 1)].map((day, index) => {
          const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day || 1);
          const citasDia = getCitasForDate(date);
          const isToday = date.toDateString() === today.toDateString();

          return (
            <div
              key={index}
              className={`p-2 border rounded-lg ${day ? 'cursor-pointer hover:bg-indigo-50' : 'opacity-0'}`}
              onClick={() => day && handleOpenModal(date)}
            >
              {day && (
                <>
                  <div className={`text-xs font-bold ${isToday ? 'text-blue-600' : ''}`}>{day}</div>
                  {citasDia.length > 0 && (
                    <div className="mt-1 space-y-1">
                      {citasDia.slice(0, 2).map((cita) => (
                        <div key={cita._id || cita.id} className="text-[10px] text-gray-600 bg-yellow-50 rounded px-1 truncate">
                          {getPacienteNombre(cita.pacienteId)}
                        </div>
                      ))}
                      {citasDia.length > 2 && (
                        <div className="text-[10px] text-gray-400">+{citasDia.length - 2} más</div>
                      )}
                    </div>
                  )}
                </>
              )}
            </div>
          );
        })}
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">
              Nueva Cita - {selectedDate.toLocaleDateString('es-ES')}
            </h3>
            <div className="space-y-3">
              <select name="pacienteId" value={newAppointment.pacienteId} onChange={handleChange} className="w-full border rounded px-3 py-2">
                <option value="">Seleccionar Paciente</option>
                {pacientes.map(p => (
                  <option key={p._id} value={p._id}>{p.nombre} {p.apellido}</option>
                ))}
              </select>
              <input name="motivo" placeholder="Motivo" value={newAppointment.motivo} onChange={handleChange} className="w-full border rounded px-3 py-2" />
              <input name="hora" type="time" value={newAppointment.hora} onChange={handleChange} className="w-full border rounded px-3 py-2" />
              <select name="estado" value={newAppointment.estado} onChange={handleChange} className="w-full border rounded px-3 py-2">
                <option value="pendiente">Pendiente</option>
                <option value="confirmada">Confirmada</option>
                <option value="completada">Completada</option>
                <option value="cancelada">Cancelada</option>
              </select>
              <div className="flex justify-end space-x-2 pt-3">
                <button onClick={() => setShowModal(false)} className="px-4 py-2 border rounded">Cancelar</button>
                <button onClick={handleSubmit} className="px-4 py-2 bg-blue-600 text-white rounded">Guardar</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CalendarView;
