import React, { useState } from 'react';
import MedicalRecord from './MedicalRecord';
import Odontograma from './Odontograma';
import ImageManager from './ImageManager';
import InsuranceIntegration from './InsuranceIntegration';
import WhatsAppIntegration from './WhatsAppIntegration';
import RemindersManager from './RemindersManager';
import RecipeEditor from './RecipeEditor';

const FichaOrtodoncia = ({ paciente }) => (
  <div className="bg-white p-4 rounded-lg shadow mb-4">
    <h3 className="text-lg font-semibold mb-2">Ficha Ortodóntica</h3>
    <p>Información ortodóntica de: {paciente.nombre}</p>
  </div>
);

const PatientDetailView = ({ patient, onUpdateOdontograma }) => {
  const [showRecipeEditor, setShowRecipeEditor] = useState(false);

  if (!patient) return null;

  return (
    <div className="space-y-6">
      <div className="bg-white p-4 rounded-lg shadow">
        <h2 className="text-2xl font-semibold mb-2">Detalle de {patient.nombre}</h2>
        <p className="text-gray-600">RUT: {patient.rut} | Edad: {patient.edad}</p>
      </div>

      <MedicalRecord patient={patient} />
      <FichaOrtodoncia paciente={patient} />

      <Odontograma
        data={Array.isArray(patient.odontograma) ? patient.odontograma : []}
        onUpdate={(updated) => onUpdateOdontograma(updated)}
      />

      <ImageManager patientId={patient.id} />
      <InsuranceIntegration patientId={patient.id} />
      <WhatsAppIntegration patient={patient} />
      <RemindersManager patientId={patient.id} />

      <div className="flex space-x-3">
        <button
          onClick={() => setShowRecipeEditor(true)}
          className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700"
        >
          Generar Receta
        </button>
      </div>

      {showRecipeEditor && (
        <RecipeEditor
          patient={patient}
          onSaveRecipe={() => setShowRecipeEditor(false)}
        />
      )}
    </div>
  );
};

export default PatientDetailView;
