import React, { useState } from 'react';

const RemindersManager = () => {
  const [reminders, setReminders] = useState([]);
  const [newReminder, setNewReminder] = useState({
    title: '',
    date: '',
    time: '',
    patientId: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewReminder({ ...newReminder, [name]: value });
  };

  const addReminder = () => {
    if (!newReminder.title) return;
    const reminder = {
      ...newReminder,
      id: Date.now(),
      completed: false
    };
    setReminders([...reminders, reminder]);
    setNewReminder({ title: '', date: '', time: '', patientId: '' });
  };

  return (
    <div className="bg-white rounded-xl shadow-sm p-6">
      <h2 className="text-xl font-semibold mb-6">Recordatorios</h2>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <input
          type="text"
          name="title"
          value={newReminder.title}
          onChange={handleInputChange}
          placeholder="TÃ­tulo"
          className="col-span-2 px-3 py-2 border border-gray-300 rounded-lg"
        />
        <input
          type="date"
          name="date"
          value={newReminder.date}
          onChange={handleInputChange}
          className="px-3 py-2 border border-gray-300 rounded-lg"
        />
        <input
          type="time"
          name="time"
          value={newReminder.time}
          onChange={handleInputChange}
          className="px-3 py-2 border border-gray-300 rounded-lg"
        />
      </div>

      <button
        onClick={addReminder}
        className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 mb-6"
      >
        Agregar Recordatorio
      </button>

      {reminders.length > 0 ? (
        <div className="space-y-3">
          {reminders.map(reminder => (
            <div key={reminder.id} className="flex items-center p-3 border border-gray-200 rounded-lg">
              <div className="flex-1">
                <p className="font-medium">{reminder.title}</p>
                <p className="text-sm text-gray-500">
                  {reminder.date} a las {reminder.time}
                </p>
              </div>
              <button className="ml-2 p-1 text-blue-600 hover:text-blue-800">
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </button>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-gray-500 text-center py-8">No hay recordatorios programados</p>
      )}
    </div>
  );
};

export default RemindersManager;
